}

console.log("[Messages API] Message inserted with ID:", insertedMessage.id);

// Send the actual message based on type
let sendSuccess = false;
let sendError = null;

try {
  switch (messageType) {
    case "sms":
    case "whatsapp":
      // Send via Twilio
      if (process.env.TWILIO_ACCOUNT_SID && process.env.TWILIO_AUTH_TOKEN) {
        const twilioClient = twilio(
          process.env.TWILIO_ACCOUNT_SID,
          process.env.TWILIO_AUTH_TOKEN,
        );

        const fromNumber =
          messageType === "whatsapp"
            ? `whatsapp:${process.env.TWILIO_WHATSAPP_FROM || process.env.TWILIO_WHATSAPP_NUMBER}`
            : process.env.TWILIO_SMS_FROM ||
              process.env.TWILIO_PHONE_NUMBER;

        const toNumber =
          messageType === "whatsapp" &&
          !recipientAddress.startsWith("whatsapp:")
            ? `whatsapp:${recipientAddress}`
            : recipientAddress;

        if (!fromNumber) {
          console.error(
            "[Messages API] Twilio from number not configured for",
            messageType,
            {
              TWILIO_SMS_FROM: process.env.TWILIO_SMS_FROM,
              TWILIO_PHONE_NUMBER: process.env.TWILIO_PHONE_NUMBER,
              TWILIO_WHATSAPP_FROM: process.env.TWILIO_WHATSAPP_FROM,
              TWILIO_WHATSAPP_NUMBER: process.env.TWILIO_WHATSAPP_NUMBER,
            },
          );
          throw new Error(
            `Twilio from number not configured for ${messageType}`,
          );
        }

        console.log("[Messages API] Sending Twilio message:", {
          messageType,
          from: fromNumber,
          to: toNumber,
          bodyLength: messageContent.length,
          messageId: insertedMessage.id,
        });

        const twilioMessage = await twilioClient.messages.create({
          body: messageContent,
          from: fromNumber,
          to: toNumber,
        });

        if (twilioMessage.sid) {
          sendSuccess = true;
          console.log("[Messages API] Twilio message sent successfully:", {
            messageId: insertedMessage.id,
            twilioSid: twilioMessage.sid,
            status: twilioMessage.status,
          });

          const { error: updateError } = await supabase
            .from("messages")
            .update({
              status: "sent",
              sent_at: new Date().toISOString(),
              twilio_sid: twilioMessage.sid,
            })
            .eq("id", insertedMessage.id);

          if (updateError) {
            console.error(
              "[Messages API] Error updating message status:",
              updateError,
            );
          } else {
            console.log("[Messages API] Message status updated to 'sent'");
          }
        }
      } else {
        // If Twilio not configured, just mark as sent
        console.log(
          "[Messages API] Twilio not configured, marking as sent",
        );
        sendSuccess = true;
        const { error: updateError } = await supabase
          .from("messages")
          .update({
            status: "sent",
            sent_at: new Date().toISOString(),
          })
          .eq("id", insertedMessage.id);

        if (updateError) {
          console.error(
            "[Messages API] Error updating message status:",
            updateError,
          );
        }
      }
      break;

    case "email":
      // Send via Resend
      if (process.env.RESEND_API_KEY) {
        const resend = new Resend(process.env.RESEND_API_KEY);

        const emailResult = await resend.emails.send({
          from:
            process.env.RESEND_FROM_EMAIL ||
            "Atlas Fitness <noreply@atlas-fitness.com>",
          to: recipientAddress,
          subject: subject || "Message from Atlas Fitness",
          html: `<div>${messageContent.replace(/\n/g, "<br>")}</div>`,
        });

        if (emailResult.data?.id) {
          sendSuccess = true;
          await supabase
            .from("messages")
            .update({
              status: "sent",
              sent_at: new Date().toISOString(),
              resend_id: emailResult.data.id,
            })
            .eq("id", insertedMessage.id);
        }
      } else {
        // If Resend not configured, just mark as sent
        sendSuccess = true;
        await supabase
          .from("messages")
          .update({
            status: "sent",
            sent_at: new Date().toISOString(),
          })
          .eq("id", insertedMessage.id);
      }
      break;

    default:
      // For other types, just mark as sent
      sendSuccess = true;
      await supabase
        .from("messages")
        .update({
          status: "sent",
          sent_at: new Date().toISOString(),
        })
        .eq("id", insertedMessage.id);
      break;
  }
} catch (error) {
  console.error(`[Messages API] Error sending ${messageType} message:`, {
    error,
    messageId: insertedMessage.id,
    messageType,
    recipientAddress,
  });
  sendError = error;

  // Update message status to failed
  await supabase
    .from("messages")
    .update({
      status: "failed",
      error_message: sendError?.toString() || "Unknown error",
      updated_at: new Date().toISOString(),
    })
    .eq("id", insertedMessage.id);
}

// Fetch the updated message to return with correct status
const { data: updatedMessage } = await supabase
  .from("messages")
  .select()
  .eq("id", insertedMessage.id)
  .single();

console.log("[Messages API] Final message status:", {
  messageId: updatedMessage?.id || insertedMessage.id,
  status: updatedMessage?.status || insertedMessage.status,
  success: sendSuccess,
});

return NextResponse.json({
  success: true,
  message: updatedMessage || insertedMessage,

      msg.sender_type || (msg.direction === "inbound" ? "member" : "gym"),
    sender_id: msg.sender_id || msg.user_id || "",
    sender_name: msg.sender_name || "",
    created_at: msg.created_at,
    read: msg.read || msg.status === "read" || false,
    type: msg.type,
    direction: msg.direction,
    status: msg.status,
  };
};

const loadConversations = async () => {
  try {
    // Get all coaching conversations
    const { data: coachingData } = await supabase.rpc(
      "get_coach_conversations",
      {
        coach_user_id: userData.id,
      },
    );

    const coachingConversations = (coachingData || []).map((conv: any) => ({
      id: `coach-${conv.member_id}`,
      contact_id: conv.member_id,
      contact_name: conv.member_name,
      contact_email: conv.member_email,
      contact_phone: conv.member_phone || "",
      last_message: conv.last_message,
      last_message_time: conv.last_message_time,
      unread_count: conv.unread_count,
      sender_type: conv.sender_type,
      type: "coaching" as const,
      membership_status: "Member",
    }));

    // Get all general conversations from leads
    const { data: leads } = await supabase
      .from("leads")
      .select("*")
      .eq("org_id", userData.organization_id)
      .order("updated_at", { ascending: false })
      .limit(50);

    // Get recent messages for leads
    const { data: recentMessages } = await supabase
      .from("messages")
      .select("*")
      .eq("organization_id", userData.organization_id)
      .order("created_at", { ascending: false })
      .limit(200);

    const generalConversations = (leads || [])
      .map((lead: any) => {
        const leadMessages = (recentMessages || []).filter(
          (m) => m.lead_id === lead.id,
        );
        const lastMessage = leadMessages[0];
        const unreadCount = leadMessages.filter(
          (m) => m.direction === "inbound" && m.status !== "read",
        ).length;

        return {
          id: `general-${lead.id}`,
          contact_id: lead.id,
          contact_name:
            `${lead.first_name || ""} ${lead.last_name || ""}`.trim() ||
            lead.name ||
            "Unknown",
          contact_email: lead.email || "",
          contact_phone: lead.phone || "",
          last_message: lastMessage?.body || "No messages yet",
          last_message_time: lastMessage?.created_at || lead.created_at,
          unread_count: unreadCount,
          sender_type:
            lastMessage?.direction === "inbound" ? "member" : "gym",
          type: "general" as const,
          membership_status: lead.status === "converted" ? "Member" : "Lead",
        };
      })
      .filter((conv: any) => conv.last_message !== "No messages yet");

    // Combine and sort all conversations
    const allConversations = [
      ...coachingConversations,
      ...generalConversations,
    ].sort(
      (a, b) =>
        new Date(b.last_message_time).getTime() -
        new Date(a.last_message_time).getTime(),
    );

    setConversations(allConversations);
  } catch (error) {
    console.error("Error loading conversations:", error);
  }
};

const loadMessages = async (contactId: string) => {
  try {
    const formattedMessages: Message[] = [];

    // Load coaching messages
    const { data: coachingMessages } = await supabase
      .from("member_coach_messages")
      .select("*")
      .or(
        `member_id.eq.${contactId},member_id.eq.${contactId.replace("lead-", "")}`,
      )
      .eq("coach_id", userData.id)
      .order("created_at", { ascending: true });

    if (coachingMessages) {
      formattedMessages.push(...coachingMessages.map(formatMessage));
    }

    // Load general messages
    const { data: generalMessages } = await supabase
      .from("messages")
      .select("*")
      .eq("lead_id", contactId.replace("lead-", ""))
      .eq("organization_id", userData.organization_id)
      .order("created_at", { ascending: true });

    if (generalMessages) {
      formattedMessages.push(...generalMessages.map(formatMessage));
    }

    // Sort all messages by time
    formattedMessages.sort(
      (a, b) =>
        new Date(a.created_at).getTime() - new Date(b.created_at).getTime(),
    );

    setMessages(formattedMessages);
  } catch (error) {
    console.error("Error loading messages:", error);
  }
};

