      await supabase
        .from("messages")
        .update({
          status: "failed",
          error_message: sendError?.toString() || "Unknown error",
          updated_at: new Date().toISOString(),
        })
        .eq("id", insertedMessage.id);
    }

    // Fetch the updated message to return with correct status
    const { data: updatedMessage } = await supabase
      .from("messages")
      .select()
      .eq("id", insertedMessage.id)
      .single();

