    await migrationService.resolveMigrationConflict(
      conflictId,
      resolution,
      user.id
    );

    return NextResponse.json({
      success: true,
      message: 'Conflict resolved'
    });

  } catch (error) {
    console.error('Conflict resolution error:', error);
    return NextResponse.json(
      { success: false, error: 'Failed to resolve conflict' },
      { status: 500 }
    );
  }
}

