import { createClient } from '@/app/lib/supabase/server';
import { OpenAI } from 'openai';
import * as XLSX from 'xlsx';
import Papa from 'papaparse';

const openai = new OpenAI({
  apiKey: process.env.OPENAI_API_KEY!,
});

interface FieldMapping {
  source_field: string;
  target_field: string;
  target_table: string;
  data_type: string;
  transformation_rule?: any;
  ai_confidence: number;
  sample_values: any[];
}

export async function POST(request: NextRequest) {
  try {
    const { jobId } = await request.json();

    if (!jobId) {
      return NextResponse.json(
        { success: false, error: 'Missing job ID' },
        { status: 400 }
      );
    }

    const supabase = createClient();

    // Get the migration job and file
    const { data: job, error: jobError } = await supabase
      .from('migration_jobs')
      .select(`
        *,
        migration_files(*)
      `)
      .eq('id', jobId)
      .single();

    if (jobError || !job) {
      return NextResponse.json(
        { success: false, error: 'Migration job not found' },
        { status: 404 }
      );
    }

    const file = job.migration_files[0];
    if (!file) {
      return NextResponse.json(
        { success: false, error: 'No file found for this job' },
        { status: 404 }
      );
    }

    // Update job status
    await supabase
      .from('migration_jobs')
      .update({ status: 'analyzing' })
      .eq('id', jobId);

    // Download file from storage
    const { data: fileData, error: downloadError } = await supabase.storage
      .from('migrations')
      .download(file.storage_path);

    if (downloadError || !fileData) {
      throw new Error('Failed to download file');
    }

    // Parse the file based on type
    let parsedData: any[] = [];
    let headers: string[] = [];

    if (file.file_type.includes('csv')) {
      const text = await fileData.text();
      const result = Papa.parse(text, { header: true, skipEmptyLines: true });
      parsedData = result.data;
      headers = result.meta.fields || [];
    } else {
      // Excel file
      const buffer = await fileData.arrayBuffer();
      const workbook = XLSX.read(buffer, { type: 'array' });
      const firstSheet = workbook.Sheets[workbook.SheetNames[0]];
      const jsonData = XLSX.utils.sheet_to_json(firstSheet);
      parsedData = jsonData;
      headers = jsonData.length > 0 ? Object.keys(jsonData[0]) : [];
    }

    // Store parsed data
    await supabase
      .from('migration_files')
      .update({
        parsed_headers: headers,
        row_count: parsedData.length,
        raw_data: { sample: parsedData.slice(0, 10) }, // Store sample for reference
        status: 'parsed'
      })
      .eq('id', file.id);

    // Use AI to analyze and map fields
    const mappings = await analyzeFieldsWithAI(headers, parsedData.slice(0, 5));

    // Store field mappings
    for (const mapping of mappings) {
      await supabase
        .from('migration_field_mappings')
        .insert({
          migration_job_id: jobId,
          organization_id: job.organization_id,
          ...mapping
        });
    }

    // Detect record types and counts
    const recordTypes = detectRecordTypes(parsedData, mappings);

    // Update job with analysis results
    await supabase
      .from('migration_jobs')
      .update({
        status: 'mapping',
        total_records: parsedData.length,
        ai_analysis: {
          detected_types: recordTypes,
          confidence_score: mappings.reduce((acc, m) => acc + m.ai_confidence, 0) / mappings.length,
          headers_found: headers.length,
          sample_size: parsedData.length
        },
        field_mappings: mappings
      })
      .eq('id', jobId);

    return NextResponse.json({
      success: true,
      mappings,
      recordTypes,
      totalRecords: parsedData.length
    });

  } catch (error) {
    console.error('Analysis error:', error);
    return NextResponse.json(
      { success: false, error: 'Failed to analyze file' },
      { status: 500 }
    );
  }
}

async function analyzeFieldsWithAI(
  headers: string[],
  sampleData: any[]
): Promise<FieldMapping[]> {
  const prompt = `
    Analyze these data fields from a GoTeamUp export and map them to our fitness CRM database.

    Headers: ${headers.join(', ')}

    Sample data (first 5 rows):
    ${JSON.stringify(sampleData, null, 2)}

    Map each field to the appropriate target in our database:
    - clients table: name, email, phone, address, date_of_birth, emergency_contact, medical_notes
    - payments table: amount, payment_date, payment_method, status, description
    - attendances table: class_date, class_name, check_in_time, status
    - forms table: form_type, submitted_at, form_data

    Return a JSON array of mappings with this structure:
    {
      "source_field": "original field name",
      "target_field": "database column name",
      "target_table": "table name",
      "data_type": "string|number|date|boolean|json",
      "transformation_rule": null or transformation logic,
      "ai_confidence": 0.0 to 1.0,
      "sample_values": [sample values from data]
    }
  `;

  try {
    const response = await openai.chat.completions.create({
      model: 'gpt-4o-mini',
      messages: [
        {
          role: 'system',
          content: 'You are a data mapping expert specializing in gym management systems. Return only valid JSON.'
        },
        {
          role: 'user',
          content: prompt
        }
      ],
      response_format: { type: 'json_object' },
      temperature: 0.1
    });

    const result = JSON.parse(response.choices[0].message.content || '{"mappings": []}');
    return result.mappings || [];
  } catch (error) {
    console.error('AI analysis failed:', error);

    // Fallback to rule-based mapping
    return fallbackFieldMapping(headers, sampleData);
  }
}

function fallbackFieldMapping(headers: string[], sampleData: any[]): FieldMapping[] {
  const mappings: FieldMapping[] = [];

  const fieldPatterns = {
    // Client fields
    name: /^(name|full_name|member_name|customer|client)$/i,
    email: /^(email|email_address|e-mail|mail)$/i,
    phone: /^(phone|mobile|cell|telephone|contact)$/i,
    address: /^(address|street|location)$/i,
    date_of_birth: /^(dob|date_of_birth|birth_date|birthday)$/i,

    // Payment fields
    amount: /^(amount|payment|total|price|cost)$/i,
    payment_date: /^(date|payment_date|transaction_date|paid_date)$/i,
    payment_method: /^(method|payment_method|payment_type|card_type)$/i,

    // Attendance fields
    class_date: /^(class_date|session_date|attendance_date|date)$/i,
    class_name: /^(class|class_name|session|activity|service)$/i,
    check_in: /^(check_in|checkin|arrival|sign_in)$/i
  };

  for (const header of headers) {
    let mapped = false;

    for (const [targetField, pattern] of Object.entries(fieldPatterns)) {
      if (pattern.test(header)) {
        const targetTable = 
          ['name', 'email', 'phone', 'address', 'date_of_birth'].includes(targetField) ? 'clients' :
          ['amount', 'payment_date', 'payment_method'].includes(targetField) ? 'payments' :
          'attendances';

        mappings.push({
          source_field: header,
          target_field: targetField,
          target_table: targetTable,
          data_type: detectDataType(sampleData, header),
          ai_confidence: 0.7,
          sample_values: sampleData.slice(0, 3).map(row => row[header]).filter(Boolean)
        });
        mapped = true;
        break;
      }
    }

    if (!mapped) {
      // Unknown field - store for manual review
      mappings.push({
        source_field: header,
        target_field: '',
        target_table: '',
        data_type: detectDataType(sampleData, header),
        ai_confidence: 0.0,
        sample_values: sampleData.slice(0, 3).map(row => row[header]).filter(Boolean)
      });
    }
  }

  return mappings;
}

function detectDataType(data: any[], field: string): string {
  const samples = data.map(row => row[field]).filter(v => v !== null && v !== undefined);

