    });

  } catch (error) {
    console.error('Processing error:', error);
    return NextResponse.json(
      { success: false, error: 'Failed to start migration' },
      { status: 500 }
    );
  }
}

async function processInBackground(
  jobId: string,
  job: any,
  mappings: any[]
) {
  try {
    const file = job.migration_files[0];

    // Download and parse file
    const { data: fileData } = await supabaseAdmin.storage
      .from('migrations')
      .download(file.storage_path);

    if (!fileData) {
      throw new Error('Failed to download file');
    }

    let parsedData: any[] = [];

    if (file.file_type.includes('csv')) {
      const text = await fileData.text();
      const result = Papa.parse(text, { header: true, skipEmptyLines: true });
      parsedData = result.data;
    } else {
      const buffer = await fileData.arrayBuffer();
      const workbook = XLSX.read(buffer, { type: 'array' });
      const firstSheet = workbook.Sheets[workbook.SheetNames[0]];
      parsedData = XLSX.utils.sheet_to_json(firstSheet);
    }

    // Process data in batches to avoid memory issues
    const batchSize = 100;
    let processed = 0;
    let successful = 0;
    let failed = 0;

    // Group mappings by table
    const tableMappings = mappings.reduce((acc, m) => {
      if (m.target_table) {
        acc[m.target_table] = acc[m.target_table] || [];
        acc[m.target_table].push(m);
      }
      return acc;
    }, {} as Record<string, any[]>);

    // Process clients first (as other records may reference them)
    if (tableMappings.clients) {
      const clientResults = await processClients(
        parsedData,
        tableMappings.clients,
        job.organization_id,
        jobId
      );
      processed += clientResults.processed;
      successful += clientResults.successful;
      failed += clientResults.failed;
    }

    // Process payments
    if (tableMappings.payments) {
      const paymentResults = await processPayments(
        parsedData,
        tableMappings.payments,
        job.organization_id,
        jobId
      );
      processed += paymentResults.processed;
      successful += paymentResults.successful;
      failed += paymentResults.failed;
    }

    // Process attendances
    if (tableMappings.attendances) {
      const attendanceResults = await processAttendances(
        parsedData,
        tableMappings.attendances,
        job.organization_id,
        jobId
      );
      processed += attendanceResults.processed;
      successful += attendanceResults.successful;
      failed += attendanceResults.failed;
    }

    // Update job as completed
    await supabaseAdmin
      .from('migration_jobs')
      .update({
        status: 'completed',
        processed_records: processed,
        successful_records: successful,
        failed_records: failed,
        completed_at: new Date().toISOString()
      })
      .eq('id', jobId);

    // Log completion
    await supabaseAdmin
      .from('migration_logs')
      .insert({
        migration_job_id: jobId,
        organization_id: job.organization_id,
        level: 'info',
        message: `Migration completed: ${successful} successful, ${failed} failed out of ${processed} processed`,
        details: { processed, successful, failed }
      });

  } catch (error) {
    console.error('Background processing error:', error);

    // Update job as failed
    await supabaseAdmin
      .from('migration_jobs')
      .update({
        status: 'failed',
        completed_at: new Date().toISOString()
      })
      .eq('id', jobId);

    // Log error
    await supabaseAdmin
      .from('migration_logs')
      .insert({
        migration_job_id: jobId,
        organization_id: job.organization_id,
        level: 'error',
        message: 'Migration failed',
        details: { error: error instanceof Error ? error.message : 'Unknown error' }
      });
  }
}

async function processClients(
  data: any[],
  mappings: any[],
  organizationId: string,
  jobId: string
) {
  let processed = 0;
  let successful = 0;
  let failed = 0;

  // Extract unique clients based on email
  const emailMapping = mappings.find(m => m.target_field === 'email');
  if (!emailMapping) {
    console.error('No email field mapping found');
    return { processed, successful, failed };
  }

  const uniqueClients = new Map();

  for (const row of data) {
    const email = row[emailMapping.source_field];
    if (email && !uniqueClients.has(email)) {
      uniqueClients.set(email, row);
    }
  }

  // Process each unique client
  for (const [email, row] of uniqueClients) {
    processed++;

    try {
      // Check for existing client
      const { data: existing } = await supabaseAdmin
        .from('clients')
        .select('id')
        .eq('email', email)
        .eq('organization_id', organizationId)
        .single();

      if (existing) {
        // Record as duplicate
        await supabaseAdmin
          .from('migration_records')
          .insert({
            migration_job_id: jobId,
            organization_id: organizationId,
            source_record_id: email,
            record_type: 'client',
            source_data: row,
            status: 'duplicate',
            target_id: existing.id,
            target_table: 'clients'
          });

        await supabaseAdmin
          .from('migration_conflicts')
          .insert({
            migration_job_id: jobId,
            organization_id: organizationId,
            migration_record_id: null,
            conflict_type: 'duplicate_email',
            existing_record_id: existing.id,
            existing_data: existing,
            incoming_data: row,
            resolution_strategy: 'skip'
          });

        continue;
      }

      // Map fields to client data
      const clientData: any = {
        organization_id: organizationId,
        created_at: new Date().toISOString()
      };

      for (const mapping of mappings) {
        if (mapping.source_field && row[mapping.source_field] !== undefined) {
          const value = transformValue(
            row[mapping.source_field],
            mapping.data_type,
            mapping.transformation_rule
          );

          if (value !== null) {
            clientData[mapping.target_field] = value;
          }
        }
      }

      // Ensure required fields
      if (!clientData.email) {
        throw new Error('Email is required');
      }

      // Create client
      const { data: newClient, error: insertError } = await supabaseAdmin
        .from('clients')
        .insert(clientData)
        .select()
        .single();

      if (insertError) {
        throw insertError;
      }

      // Record success
      await supabaseAdmin
        .from('migration_records')
        .insert({
          migration_job_id: jobId,
          organization_id: organizationId,
          source_record_id: email,
          record_type: 'client',
          source_data: row,
          mapped_data: clientData,
          target_id: newClient.id,
          target_table: 'clients',
          status: 'success',
          processed_at: new Date().toISOString()
        });

      successful++;

    } catch (error) {
      failed++;

      // Record failure
      await supabaseAdmin
        .from('migration_records')
        .insert({
          migration_job_id: jobId,
          organization_id: organizationId,
          source_record_id: email,
          record_type: 'client',
          source_data: row,
          status: 'failed',
          error_message: error instanceof Error ? error.message : 'Unknown error',
          error_details: { error }
        });

      // Log error
      await supabaseAdmin
        .from('migration_logs')
        .insert({
          migration_job_id: jobId,
          organization_id: organizationId,
          level: 'error',
          message: `Failed to import client: ${email}`,
          details: { email, error: error instanceof Error ? error.message : 'Unknown error' }
        });
    }

    // Update progress periodically
    if (processed % 10 === 0) {
      await supabaseAdmin
        .from('migration_jobs')
        .update({
          processed_records: processed,
          successful_records: successful,
          failed_records: failed
        })
        .eq('id', jobId);
    }
  }

  return { processed, successful, failed };
}

async function processPayments(
  data: any[],
  mappings: any[],
  organizationId: string,
  jobId: string
) {
  let processed = 0;
  let successful = 0;
  let failed = 0;

  // Process each payment row
  for (const row of data) {
    processed++;

    try {
      // Map fields to payment data
      const paymentData: any = {
        organization_id: organizationId,
        created_at: new Date().toISOString()
      };

      for (const mapping of mappings) {
        if (mapping.source_field && row[mapping.source_field] !== undefined) {
          const value = transformValue(
            row[mapping.source_field],
            mapping.data_type,
            mapping.transformation_rule
          );

          if (value !== null) {
            paymentData[mapping.target_field] = value;
          }
        }
      }

      // Try to match with existing client
      const emailField = data.find(m => m.target_field === 'email')?.source_field;
      if (emailField && row[emailField]) {
        const { data: client } = await supabaseAdmin
          .from('clients')
          .select('id')
          .eq('email', row[emailField])
          .eq('organization_id', organizationId)
          .single();

        if (client) {
          paymentData.client_id = client.id;
        }
      }

      // Create payment record
      const { data: newPayment, error: insertError } = await supabaseAdmin
        .from('payments')
        .insert(paymentData)
        .select()
        .single();

      if (insertError) {
        throw insertError;
      }

      successful++;

    } catch (error) {
      failed++;

      // Log error
      await supabaseAdmin
        .from('migration_logs')
        .insert({
          migration_job_id: jobId,
          organization_id: organizationId,
          level: 'error',
          message: 'Failed to import payment',
          details: { row, error: error instanceof Error ? error.message : 'Unknown error' }
        });
    }
  }

  return { processed, successful, failed };
}

async function processAttendances(
  data: any[],
  mappings: any[],
  organizationId: string,
  jobId: string
) {
  let processed = 0;
  let successful = 0;
  let failed = 0;

  // Similar to processPayments but for attendance records
  // Implementation would follow the same pattern

  return { processed, successful, failed };
}

function transformValue(value: any, dataType: string, transformationRule?: any): any {
  if (value === null || value === undefined || value === '') {
    return null;
  }

  switch (dataType) {
    case 'date':
      const date = new Date(value);
      return isNaN(date.getTime()) ? null : date.toISOString();

    case 'number':
      const num = Number(value);
      return isNaN(num) ? null : num;

    case 'boolean':
      const lower = String(value).toLowerCase();
      return ['true', '1', 'yes', 'y'].includes(lower);

    case 'json':
      try {
        return typeof value === 'string' ? JSON.parse(value) : value;
      } catch {
        return value;
      }

    default:
      return String(value).trim();
  }
}

