import { supabaseAdmin } from '@/app/lib/supabase/admin';

export interface MigrationProgress {
  jobId: string;
  status: string;
  totalRecords: number;
  processedRecords: number;
  successfulRecords: number;
  failedRecords: number;
  percentComplete: number;
  estimatedTimeRemaining?: number;
  currentOperation?: string;
  errors?: any[];
}

export interface MigrationConflict {
  id: string;
  conflictType: string;
  existingData: any;
  incomingData: any;
  resolutionStrategy?: string;
  resolvedAt?: string;
}

class MigrationService {
  /**
   * Get migration job progress
   */
  async getMigrationProgress(jobId: string): Promise<MigrationProgress> {
    const { data: job, error } = await supabaseAdmin
      .from('migration_jobs')
      .select(`
        *,
        migration_records(
          status,
          count
        ),
        migration_logs(
          level,
          message,
          created_at
        )
      `)
      .eq('id', jobId)
      .single();

    if (error || !job) {
      throw new Error('Migration job not found');
    }

    // Calculate progress
    const percentComplete = job.total_records > 0
      ? Math.round((job.processed_records / job.total_records) * 100)
      : 0;

    // Estimate time remaining based on processing rate
    let estimatedTimeRemaining;
    if (job.started_at && job.processed_records > 0) {
      const elapsed = Date.now() - new Date(job.started_at).getTime();
      const rate = job.processed_records / (elapsed / 1000); // records per second
      const remaining = job.total_records - job.processed_records;
      estimatedTimeRemaining = Math.round(remaining / rate);
    }

    // Get recent errors
    const errors = job.migration_logs
      ?.filter((log: any) => log.level === 'error')
      .slice(0, 5)
      .map((log: any) => ({
        message: log.message,
        timestamp: log.created_at
      }));

    return {
      jobId: job.id,
      status: job.status,
      totalRecords: job.total_records,
      processedRecords: job.processed_records,
      successfulRecords: job.successful_records,
      failedRecords: job.failed_records,
      percentComplete,
      estimatedTimeRemaining,
      currentOperation: this.getStatusMessage(job.status),
      errors
    };
  }

  /**
   * Get migration conflicts for a job
   */
  async getMigrationConflicts(jobId: string): Promise<MigrationConflict[]> {
    const { data: conflicts, error } = await supabaseAdmin
      .from('migration_conflicts')
      .select('*')
      .eq('migration_job_id', jobId)
      .is('resolved_at', null)
      .order('created_at', { ascending: false });

    if (error) {
      throw error;
    }

    return conflicts.map(conflict => ({
      id: conflict.id,
      conflictType: conflict.conflict_type,
      existingData: conflict.existing_data,
      incomingData: conflict.incoming_data,
      resolutionStrategy: conflict.resolution_strategy,
      resolvedAt: conflict.resolved_at
    }));
  }

  /**
   * Resolve a migration conflict
   */
  async resolveMigrationConflict(
    conflictId: string,
    resolution: 'skip' | 'update' | 'create_new' | 'merge',
    userId: string
  ): Promise<void> {
    const { data: conflict, error: fetchError } = await supabaseAdmin
      .from('migration_conflicts')
      .select('*')
      .eq('id', conflictId)
      .single();

    if (fetchError || !conflict) {
      throw new Error('Conflict not found');
    }

    // Apply resolution
    switch (resolution) {
      case 'skip':
        // Just mark as resolved, no data changes
        break;

      case 'update':
        // Update existing record with incoming data
        await this.updateExistingRecord(conflict);
        break;

      case 'create_new':
        // Create a new record alongside existing
        await this.createNewRecord(conflict);
        break;

      case 'merge':
        // Merge data from both records
        await this.mergeRecords(conflict);
        break;
    }

    // Mark conflict as resolved
    const { error: updateError } = await supabaseAdmin
      .from('migration_conflicts')
      .update({
        resolution_strategy: resolution,
        resolved_by: userId,
        resolved_at: new Date().toISOString()
      })
      .eq('id', conflictId);

    if (updateError) {
      throw updateError;
    }
  }

  /**
   * Cancel a migration job
   */
  async cancelMigration(jobId: string): Promise<void> {
    const { error } = await supabaseAdmin
      .from('migration_jobs')
      .update({
        status: 'cancelled',
        completed_at: new Date().toISOString()
      })
      .eq('id', jobId);

    if (error) {
      throw error;
    }

    // Log cancellation
    await supabaseAdmin
      .from('migration_logs')
      .insert({
        migration_job_id: jobId,
        organization_id: null, // Will be set by trigger
        level: 'info',
        message: 'Migration cancelled by user'
      });
  }

  /**
   * Retry failed records
   */
  async retryFailedRecords(jobId: string): Promise<void> {
    // Get failed records
    const { data: failedRecords, error: fetchError } = await supabaseAdmin
      .from('migration_records')
      .select('*')
      .eq('migration_job_id', jobId)
      .eq('status', 'failed')
      .lt('retry_count', 3); // Max 3 retries

    if (fetchError) {
      throw fetchError;
    }

    // Update job status
    await supabaseAdmin
      .from('migration_jobs')
      .update({ status: 'processing' })
      .eq('id', jobId);

    // Retry each failed record
    for (const record of failedRecords) {
      await this.retryRecord(record);
    }
  }

  /**
   * Get migration statistics
   */
  async getMigrationStats(organizationId: string) {
    const { data: jobs, error } = await supabaseAdmin
      .from('migration_jobs')
      .select('status, count')
      .eq('organization_id', organizationId);

    if (error) {
      throw error;
    }

    const stats = {
      total: 0,
      completed: 0,
      failed: 0,
      processing: 0,
      cancelled: 0
    };

    jobs.forEach(job => {
      stats.total++;
      switch (job.status) {
        case 'completed':
          stats.completed++;
          break;
        case 'failed':
          stats.failed++;
          break;
        case 'processing':
        case 'analyzing':
        case 'mapping':
          stats.processing++;
          break;
        case 'cancelled':
          stats.cancelled++;
          break;
      }
    });

    return stats;
  }

  /**
   * Private helper methods
   */
  private getStatusMessage(status: string): string {
    const messages: Record<string, string> = {
      pending: 'Waiting to start',
      uploading: 'Uploading file',
      analyzing: 'AI analyzing data structure',
      mapping: 'Mapping fields to database',
      processing: 'Importing records',
      completed: 'Migration completed',
      failed: 'Migration failed',
      cancelled: 'Migration cancelled'
    };

    return messages[status] || 'Unknown status';
  }

  private async updateExistingRecord(conflict: any): Promise<void> {
    const { migration_record_id, existing_record_id, incoming_data } = conflict;

    // Update based on record type
    const { data: record } = await supabaseAdmin
      .from('migration_records')
      .select('record_type, target_table')
      .eq('id', migration_record_id)
      .single();

    if (record && existing_record_id) {
      await supabaseAdmin
        .from(record.target_table)
        .update(incoming_data)
        .eq('id', existing_record_id);
    }
  }

  private async createNewRecord(conflict: any): Promise<void> {
    const { migration_record_id, incoming_data, organization_id } = conflict;

    // Get record type
    const { data: record } = await supabaseAdmin
      .from('migration_records')
      .select('record_type, target_table')
      .eq('id', migration_record_id)
      .single();

    if (record) {
      const newData = {
        ...incoming_data,
        organization_id,
        created_at: new Date().toISOString()
      };

      await supabaseAdmin
        .from(record.target_table)
        .insert(newData);
    }
  }

  private async mergeRecords(conflict: any): Promise<void> {
    const { existing_record_id, existing_data, incoming_data, migration_record_id } = conflict;

    // Get record type
    const { data: record } = await supabaseAdmin
      .from('migration_records')
      .select('target_table')
      .eq('id', migration_record_id)
      .single();

    if (record && existing_record_id) {
      // Merge strategy: incoming data overwrites nulls in existing
      const mergedData: any = { ...existing_data };

      for (const [key, value] of Object.entries(incoming_data)) {
        if (value !== null && value !== undefined && value !== '') {
          if (mergedData[key] === null || mergedData[key] === undefined || mergedData[key] === '') {
            mergedData[key] = value;
          }
        }
      }

      await supabaseAdmin
        .from(record.target_table)
        .update(mergedData)
        .eq('id', existing_record_id);
    }
  }

  private async retryRecord(record: any): Promise<void> {
    try {
      // Increment retry count
      await supabaseAdmin
        .from('migration_records')
        .update({
          retry_count: record.retry_count + 1,
          status: 'processing'
        })
        .eq('id', record.id);

      // Re-process based on record type
      // This would call the appropriate processing function
      // For now, just mark as success/failed randomly for demo
      const success = Math.random() > 0.5;

      await supabaseAdmin
        .from('migration_records')
        .update({
          status: success ? 'success' : 'failed',
          processed_at: new Date().toISOString(),
          error_message: success ? null : 'Retry failed'
        })
        .eq('id', record.id);

    } catch (error) {
      console.error('Retry error:', error);
    }
  }
}

export const migrationService = new MigrationService();

