import { OpenAI } from 'openai';

const openai = new OpenAI({
  apiKey: process.env.OPENAI_API_KEY!,
});

export interface FieldMapping {
  sourceField: string;
  sourceDataType: string;
  sampleValues: any[];
  targetTable: string;
  targetField: string;
  targetDataType: string;
  transformationType?: string;
  transformationConfig?: any;
  confidence: number;
  isRequired: boolean;
  priority: number;
}

export interface DataAnalysisResult {
  dataTypes: Record<string, number>;
  columns: any[];
  qualityScore: number;
  validRows: number;
  fieldMappings: FieldMapping[];
}

/**
 * Analyze data with AI to determine field mappings and data types
 */
export async function analyzeDataWithAI(
  data: any[],
  sourceSystem: string
): Promise<DataAnalysisResult> {
  try {
    // Get sample data for analysis
    const sampleSize = Math.min(10, data.length);
    const sampleData = data.slice(0, sampleSize);

    // Extract column names
    const columns = data.length > 0 ? Object.keys(data[0]) : [];

    // Analyze data types and patterns
    const columnAnalysis = analyzeColumns(data, columns);

    // Use AI to map fields
    const fieldMappings = await generateFieldMappings(
      columnAnalysis,
      sampleData,
      sourceSystem
    );

    // Calculate data quality score
    const qualityScore = calculateQualityScore(data, columnAnalysis);

    // Count valid rows
    const validRows = countValidRows(data, fieldMappings);

    // Count data types
    const dataTypes = countDataTypes(fieldMappings);

    return {
      dataTypes,
      columns: columnAnalysis,
      qualityScore,
      validRows,
      fieldMappings
    };
  } catch (error) {
    console.error('AI analysis error:', error);

    // Fallback to rule-based analysis
    return fallbackAnalysis(data, sourceSystem);
  }
}

function analyzeColumns(data: any[], columns: string[]) {
  return columns.map(column => {
    const values = data.map(row => row[column]).filter(v => v !== null && v !== undefined);
    const uniqueValues = new Set(values);

    return {
      name: column,
      dataType: detectDataType(values),
      uniqueCount: uniqueValues.size,
      nullCount: data.length - values.length,
      sampleValues: Array.from(uniqueValues).slice(0, 5),
      patterns: detectPatterns(values)
    };
  });
}

function detectDataType(values: any[]): string {
  if (values.length === 0) return 'unknown';

  // Check for dates
  const dateCount = values.filter(v => !isNaN(Date.parse(v))).length;
  if (dateCount / values.length > 0.8) return 'date';

  // Check for numbers
  const numberCount = values.filter(v => !isNaN(Number(v))).length;
  if (numberCount / values.length > 0.8) return 'number';

  // Check for booleans
  const booleanValues = ['true', 'false', '1', '0', 'yes', 'no'];
  const booleanCount = values.filter(v => 
    booleanValues.includes(String(v).toLowerCase())
  ).length;
  if (booleanCount / values.length > 0.8) return 'boolean';

  // Check for emails
  const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  const emailCount = values.filter(v => emailPattern.test(String(v))).length;
  if (emailCount / values.length > 0.5) return 'email';

  // Check for phones
  const phonePattern = /^[\d\s\-\(\)\+]+$/;
  const phoneCount = values.filter(v => 
    phonePattern.test(String(v)) && String(v).length >= 10
  ).length;
  if (phoneCount / values.length > 0.5) return 'phone';

  return 'string';
}

function detectPatterns(values: any[]) {
  const patterns: any = {};

  // Check for common patterns
  const stringValues = values.map(v => String(v));

  // Currency pattern
  const currencyCount = stringValues.filter(v => /^\$?[\d,]+\.?\d*$/.test(v)).length;
  if (currencyCount / values.length > 0.5) {
    patterns.currency = true;
  }

  // Percentage pattern
  const percentCount = stringValues.filter(v => /^\d+\.?\d*%$/.test(v)).length;
  if (percentCount / values.length > 0.5) {
    patterns.percentage = true;
  }

  return patterns;
}

async function generateFieldMappings(
  columnAnalysis: any[],
  sampleData: any[],
  sourceSystem: string
): Promise<FieldMapping[]> {
  const prompt = `
    You are a data mapping expert for fitness gym management systems.
    Source system: ${sourceSystem}

    Analyze these columns and map them to our database schema:

    Columns:
    ${JSON.stringify(columnAnalysis, null, 2)}

    Sample data:
    ${JSON.stringify(sampleData, null, 2)}

    Our target schema:
    - clients table: id, organization_id, name, email, phone, address, date_of_birth, emergency_contact, emergency_phone, medical_notes, tags, joined_date, status
    - payments table: id, organization_id, client_id, amount, payment_date, payment_method, status, description, invoice_number, stripe_payment_id
    - attendances table: id, organization_id, client_id, class_id, class_date, class_name, check_in_time, status
    - memberships table: id, organization_id, client_id, membership_type, start_date, end_date, status, price, billing_cycle
    - forms table: id, organization_id, client_id, form_type, form_name, submitted_at, form_data, status

    For each column, provide:
    1. Target table and field
    2. Data transformation needed
    3. Confidence score (0-1)
    4. Whether the field is required

    Common GoTeamUp field mappings:
    - "Member Name" or "Name" -> clients.name
    - "Email" or "Email Address" -> clients.email
    - "Phone" or "Mobile" -> clients.phone
    - "Date Joined" or "Join Date" -> clients.joined_date
    - "Emergency Contact" -> clients.emergency_contact
    - "Payment Amount" or "Amount" -> payments.amount
    - "Payment Date" -> payments.payment_date
    - "Class Name" or "Service" -> attendances.class_name
    - "Check-in Time" -> attendances.check_in_time

    Return a JSON array of field mappings.
  `;

  try {
    const response = await openai.chat.completions.create({
      model: 'gpt-4o-mini',
      messages: [
        {
          role: 'system',
          content: 'You are a data mapping expert. Return only valid JSON.'
        },
        {
          role: 'user',
          content: prompt
        }
      ],
      response_format: { type: 'json_object' },
      temperature: 0.1,
      max_tokens: 2000
    });

    const result = JSON.parse(response.choices[0].message.content || '{"mappings": []}');
    return (result.mappings || []).map((m: any) => ({
      sourceField: m.source_field || m.sourceField,
      sourceDataType: m.source_type || 'string',
      sampleValues: m.samples || [],
      targetTable: m.target_table || m.targetTable,
      targetField: m.target_field || m.targetField,
      targetDataType: m.target_type || 'string',
      transformationType: m.transformation || 'direct',
      transformationConfig: m.config || {},
      confidence: m.confidence || 0.5,
      isRequired: m.required || false,
      priority: m.priority || 1
    }));
  } catch (error) {
    console.error('OpenAI mapping failed:', error);
    return fallbackFieldMappings(columnAnalysis);
  }
}

function fallbackFieldMappings(columnAnalysis: any[]): FieldMapping[] {
  const mappings: FieldMapping[] = [];

  const fieldPatterns = {
    // Client fields
    name: /^(name|member_name|full_name|customer|client_name)$/i,
    email: /^(email|email_address|e-mail|member_email)$/i,
    phone: /^(phone|mobile|cell|telephone|contact_number)$/i,
    address: /^(address|street|location|home_address)$/i,
    date_of_birth: /^(dob|date_of_birth|birth_date|birthday)$/i,
    emergency_contact: /^(emergency_contact|emergency_name|ice_name)$/i,
    emergency_phone: /^(emergency_phone|emergency_number|ice_phone)$/i,
    medical_notes: /^(medical|health|conditions|allergies|notes)$/i,
    joined_date: /^(joined|join_date|start_date|member_since)$/i,

    // Payment fields
    amount: /^(amount|payment|total|price|cost|fee)$/i,
    payment_date: /^(payment_date|transaction_date|paid_date|date)$/i,
    payment_method: /^(payment_method|payment_type|card_type|method)$/i,

    // Attendance fields
    class_name: /^(class|class_name|service|activity|session_name)$/i,
    class_date: /^(class_date|session_date|attendance_date)$/i,
    check_in: /^(check_in|checkin|arrival|sign_in)$/i,

    // Membership fields
    membership_type: /^(membership|plan|subscription|package)$/i,
    start_date: /^(start|start_date|from_date|begin_date)$/i,
    end_date: /^(end|end_date|to_date|expire_date|expiry)$/i
  };

  for (const column of columnAnalysis) {
    let mapped = false;

    for (const [targetField, pattern] of Object.entries(fieldPatterns)) {
      if (pattern.test(column.name)) {
        let targetTable = 'clients';

        if (['amount', 'payment_date', 'payment_method'].includes(targetField)) {
          targetTable = 'payments';
        } else if (['class_name', 'class_date', 'check_in'].includes(targetField)) {
          targetTable = 'attendances';
        } else if (['membership_type', 'start_date', 'end_date'].includes(targetField)) {
          targetTable = 'memberships';
        }

        mappings.push({
          sourceField: column.name,
          sourceDataType: column.dataType,
          sampleValues: column.sampleValues,
          targetTable,
          targetField,
          targetDataType: column.dataType,
          confidence: 0.7,
          isRequired: ['name', 'email'].includes(targetField),
          priority: ['name', 'email'].includes(targetField) ? 1 : 2
        });

        mapped = true;
        break;
      }
    }

    if (!mapped) {
      // Unknown field - include but with low confidence
      mappings.push({
        sourceField: column.name,
        sourceDataType: column.dataType,
        sampleValues: column.sampleValues,
        targetTable: '',
        targetField: '',
        targetDataType: column.dataType,
        confidence: 0.0,
        isRequired: false,
        priority: 3
      });
    }
  }

  return mappings;
}

function calculateQualityScore(data: any[], columnAnalysis: any[]): number {
  if (data.length === 0) return 0;

  let score = 0;
  let factors = 0;

  // Factor 1: Completeness (non-null values)
  for (const column of columnAnalysis) {
    const completeness = 1 - (column.nullCount / data.length);
    score += completeness;
    factors++;
  }

  // Factor 2: Consistency (data type consistency)
  const typeConsistency = columnAnalysis.filter(c => 
    c.dataType !== 'unknown' && c.dataType !== 'string'
  ).length / columnAnalysis.length;
  score += typeConsistency;
  factors++;

  // Factor 3: Uniqueness (for ID fields)
  const idColumns = columnAnalysis.filter(c => 
    /id|email/i.test(c.name) && c.uniqueCount === data.length
  );
  if (idColumns.length > 0) {
    score += 1;
  }
  factors++;

  return factors > 0 ? score / factors : 0;
}

function countValidRows(data: any[], fieldMappings: FieldMapping[]): number {
  const requiredFields = fieldMappings.filter(m => m.isRequired);

  return data.filter(row => {
    return requiredFields.every(field => {
      const value = row[field.sourceField];
      return value !== null && value !== undefined && value !== '';
    });
  }).length;
}

function countDataTypes(fieldMappings: FieldMapping[]): Record<string, number> {
  const types: Record<string, number> = {};

  for (const mapping of fieldMappings) {
    if (mapping.targetTable) {
      types[mapping.targetTable] = (types[mapping.targetTable] || 0) + 1;
    }
  }

  return types;
}

function fallbackAnalysis(data: any[], sourceSystem: string): DataAnalysisResult {
  const columns = data.length > 0 ? Object.keys(data[0]) : [];
  const columnAnalysis = analyzeColumns(data, columns);
  const fieldMappings = fallbackFieldMappings(columnAnalysis);

  return {
    dataTypes: countDataTypes(fieldMappings),
    columns: columnAnalysis,
    qualityScore: calculateQualityScore(data, columnAnalysis),
    validRows: countValidRows(data, fieldMappings),
    fieldMappings
  };
}

