/**
 * AI Data Analyzer for GoTeamUp Migration
 * Uses OpenAI to intelligently analyze and map GoTeamUp data to Atlas Fitness schema
 */

import OpenAI from 'openai';
import { supabaseAdmin } from '../supabase/admin';

const openai = new OpenAI({
  apiKey: process.env.OPENAI_API_KEY,
});

export interface DataAnalysisResult {
  dataTypes: Record<string, string>;
  columns: ColumnAnalysis[];
  qualityScore: number;
  validRows: number;
  fieldMappings: FieldMapping[];
  recommendations: string[];
  potentialIssues: Issue[];
}

export interface ColumnAnalysis {
  name: string;
  dataType: string;
  sampleValues: string[];
  nullCount: number;
  uniqueCount: number;
  pattern: string | null;
  confidence: number;
}

export interface FieldMapping {
  sourceField: string;
  targetTable: string;
  targetField: string;
  sourceDataType: string;
  targetDataType: string;
  transformationType: 'direct' | 'format' | 'lookup' | 'split' | 'merge' | 'calculate';
  transformationConfig: any;
  confidence: number;
  isRequired: boolean;
  priority: number;
  sampleValues: string[];
}

export interface Issue {
  type: 'warning' | 'error';
  field: string;
  message: string;

