      },
      {
        name: "Website & Forms",
        href: "/settings/integrations/website",
        icon: Globe,
      },
    ],
  },
  {
    category: "AUTOMATION",
    items: [
      { name: "Workflows", href: "/settings/workflows", icon: Zap },
      { name: "AI Chatbot", href: "/settings/ai-chatbot", icon: Bot },
      { name: "Email Templates", href: "/settings/templates", icon: FileText },
    ],
  },
  {
    category: "DATA",
    items: [
      { name: "Migrations", href: "/settings/migrations", icon: Upload },
      { name: "Data & Privacy", href: "/settings/data", icon: Database },
    ],
  },
  {
