"use client";

import { useEffect, useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogDescription,
  DialogFooter,
} from "@/app/components/ui/dialog";
import { Bot, Loader2 } from "lucide-react";

const agentFormSchema = z.object({
  name: z
    .string()
    .min(1, "Name is required")
    .max(100, "Name must be less than 100 characters"),
  description: z
    .string()
    .min(1, "Description is required")
    .max(500, "Description must be less than 500 characters"),
  role: z.enum(["customer_support", "financial", "social_media", "custom"]),
  system_prompt: z.string().min(1, "System prompt is required"),
  model: z.enum([
    "gpt-4o",
    "gpt-4o-mini",
    "claude-3-5-sonnet-20241022",
    "claude-3-5-haiku-20241022",
  ]),
  temperature: z.number().min(0).max(2),
  max_tokens: z.number().min(1).max(32000).optional(),
  allowed_tools: z.array(z.string()).optional(),
});

type AgentFormData = z.infer<typeof agentFormSchema>;

interface Agent {
  id: string;
  name: string;
  description: string;
  role: string;
  system_prompt: string;
  model: string;
  temperature: number;
  max_tokens?: number;
  allowed_tools?: string[];
}

interface AgentFormModalProps {
  open: boolean;
  onClose: () => void;
  onSuccess: () => void;
  agent: Agent | null;
}

export function AgentFormModal({
  open,
  onClose,
  onSuccess,
  agent,
}: AgentFormModalProps) {
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [availableTools, setAvailableTools] = useState<any[]>([]);

  const {
    register,
    handleSubmit,
    formState: { errors },
    reset,
    watch,
    setValue,
  } = useForm<AgentFormData>({
    resolver: zodResolver(agentFormSchema),
    defaultValues: {
      name: "",
      description: "",
      role: "custom",
      system_prompt: "",
      model: "gpt-4o-mini",
      temperature: 0.7,
      max_tokens: 4000,
      allowed_tools: [],
    },
  });

  const temperature = watch("temperature");

  useEffect(() => {
    if (open) {
      loadAvailableTools();
      if (agent) {
        reset({
          name: agent.name,
          description: agent.description,
          role: agent.role as any,
          system_prompt: agent.system_prompt,
          model: agent.model as any,
          temperature: agent.temperature,
          max_tokens: agent.max_tokens,
          allowed_tools: agent.allowed_tools || [],
        });
      } else {
        reset({
          name: "",
          description: "",
          role: "custom",
          system_prompt: "",
          model: "gpt-4o-mini",
          temperature: 0.7,
          max_tokens: 4000,
          allowed_tools: [],
        });
      }
      setError(null);
    }
  }, [open, agent, reset]);

  const loadAvailableTools = async () => {
    try {
      const response = await fetch("/api/ai-agents/tools");
      const result = await response.json();
      if (result.success) {
        setAvailableTools(result.tools || []);
      }
    } catch (error) {
      console.error("Error loading tools:", error);
    }
  };

  const onSubmit = async (data: AgentFormData) => {
    setSubmitting(true);
    setError(null);

    try {
      const url = agent ? `/api/ai-agents/${agent.id}` : "/api/ai-agents";
      const method = agent ? "PUT" : "POST";

      const response = await fetch(url, {
        method,
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });

      const result = await response.json();

      if (!response.ok) {
        throw new Error(result.error || "Failed to save agent");
      }

      onSuccess();
      reset();
    } catch (err: any) {
      setError(err.message || "An error occurred");
    } finally {
      setSubmitting(false);
    }
  };

  const handleToolToggle = (toolId: string) => {
    const currentTools = watch("allowed_tools") || [];
    const updatedTools = currentTools.includes(toolId)
      ? currentTools.filter((id) => id !== toolId)
      : [...currentTools, toolId];
    setValue("allowed_tools", updatedTools);
  };

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent
        className="max-w-2xl max-h-[90vh] overflow-y-auto"
        onClose={onClose}
      >
        <DialogHeader>
          <DialogTitle>
            <div className="flex items-center gap-2">
              <Bot className="h-6 w-6 text-orange-500" />
              {agent ? "Edit AI Agent" : "Create New AI Agent"}
            </div>
          </DialogTitle>
          <DialogDescription>
            {agent
              ? "Update the configuration for this AI agent"
              : "Configure a new AI agent to assist with your gym operations"}
          </DialogDescription>
        </DialogHeader>

        <form onSubmit={handleSubmit(onSubmit)} className="space-y-6 p-6">
          {error && (
            <div className="bg-red-500/10 border border-red-500 text-red-400 px-4 py-3 rounded-lg">
              {error}
            </div>
          )}

          {/* Basic Info */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium mb-2">Name *</label>
              <input
                {...register("name")}
                type="text"
                placeholder="e.g., Support Agent"
                className="w-full px-3 py-2 bg-gray-900 border border-gray-700 rounded-lg text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-orange-500"
              />
              {errors.name && (
                <p className="text-red-400 text-sm mt-1">
                  {errors.name.message}
                </p>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium mb-2">Role *</label>
              <select
                {...register("role")}
                className="w-full px-3 py-2 bg-gray-900 border border-gray-700 rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-orange-500"
              >
                <option value="custom">Custom</option>
                <option value="customer_support">Customer Support</option>
                <option value="financial">Financial</option>
                <option value="social_media">Social Media</option>
              </select>
              {errors.role && (
                <p className="text-red-400 text-sm mt-1">
                  {errors.role.message}
                </p>
              )}
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium mb-2">
              Description *
            </label>
            <textarea
              {...register("description")}
              rows={3}
              placeholder="Describe what this agent does..."
              className="w-full px-3 py-2 bg-gray-900 border border-gray-700 rounded-lg text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-orange-500 resize-none"
            />
            {errors.description && (
              <p className="text-red-400 text-sm mt-1">
                {errors.description.message}
              </p>
            )}
          </div>

          {/* Model Configuration */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium mb-2">Model *</label>
              <select
                {...register("model")}
                className="w-full px-3 py-2 bg-gray-900 border border-gray-700 rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-orange-500"
              >
                <option value="gpt-4o-mini">GPT-4o Mini (Fast & Cheap)</option>
                <option value="gpt-4o">GPT-4o (Balanced)</option>
                <option value="claude-3-5-haiku-20241022">
                  Claude 3.5 Haiku (Fast)
                </option>
                <option value="claude-3-5-sonnet-20241022">
                  Claude 3.5 Sonnet (Advanced)
                </option>
              </select>
              {errors.model && (
                <p className="text-red-400 text-sm mt-1">
                  {errors.model.message}
                </p>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium mb-2">
                Temperature: {temperature.toFixed(2)}
              </label>
              <input
                {...register("temperature", { valueAsNumber: true })}
                type="range"
                min="0"
                max="2"
                step="0.1"
                className="w-full"
              />
              <div className="flex justify-between text-xs text-gray-400 mt-1">
                <span>Focused (0)</span>
                <span>Creative (2)</span>
              </div>
              {errors.temperature && (
                <p className="text-red-400 text-sm mt-1">
                  {errors.temperature.message}
                </p>
              )}
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium mb-2">
              Max Tokens (Optional)
            </label>
            <input
              {...register("max_tokens", { valueAsNumber: true })}
              type="number"
              min="1"
              max="32000"
              placeholder="4000"
              className="w-full px-3 py-2 bg-gray-900 border border-gray-700 rounded-lg text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-orange-500"
            />
            {errors.max_tokens && (
              <p className="text-red-400 text-sm mt-1">
                {errors.max_tokens.message}
              </p>
            )}
          </div>

          {/* System Prompt */}
          <div>
            <label className="block text-sm font-medium mb-2">
              System Prompt *
            </label>
            <textarea
              {...register("system_prompt")}
              rows={6}
              placeholder="You are a helpful AI assistant for a fitness gym. Your role is to..."
              className="w-full px-3 py-2 bg-gray-900 border border-gray-700 rounded-lg text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-orange-500 resize-none font-mono text-sm"
            />
            {errors.system_prompt && (
              <p className="text-red-400 text-sm mt-1">
                {errors.system_prompt.message}
              </p>
            )}
          </div>

          {/* Allowed Tools */}
          {availableTools.length > 0 && (
            <div>
              <label className="block text-sm font-medium mb-2">
                Allowed Tools (Optional)
              </label>
              <div className="grid grid-cols-2 gap-2 max-h-40 overflow-y-auto bg-gray-900 border border-gray-700 rounded-lg p-3">
                {availableTools.map((tool) => (
                  <label
                    key={tool.id}
                    className="flex items-center gap-2 cursor-pointer hover:bg-gray-800 p-2 rounded"
                  >
                    <input
                      type="checkbox"
                      checked={
                        watch("allowed_tools")?.includes(tool.id) || false
                      }
                      onChange={() => handleToolToggle(tool.id)}
                      className="rounded border-gray-700 text-orange-500 focus:ring-orange-500"
                    />
                    <span className="text-sm text-gray-300">{tool.name}</span>
                  </label>
                ))}
              </div>
            </div>
          )}

          {/* Actions */}
          <DialogFooter>
            <button
              type="button"
              onClick={onClose}
              className="px-4 py-2 bg-gray-700 hover:bg-gray-600 text-white rounded-lg transition-colors"
              disabled={submitting}
            >
              Cancel
            </button>
            <button
              type="submit"
              disabled={submitting}
              className="px-4 py-2 bg-orange-600 hover:bg-orange-700 text-white rounded-lg transition-colors disabled:opacity-50 disabled:cursor-not-allowed flex items-center gap-2"
            >
              {submitting && <Loader2 className="h-4 w-4 animate-spin" />}
              {agent ? "Update Agent" : "Create Agent"}
            </button>
          </DialogFooter>
        </form>
      </DialogContent>
    </Dialog>
  );
}

