"use client";

import { useState, useEffect } from "react";
import Link from "next/link";
import { usePathname, useRouter } from "next/navigation";
import { createClient } from "@/app/lib/supabase/client";
import { isFeatureEnabled } from "@/app/lib/feature-flags";
import { ComingSoonBadge } from "@/app/components/ComingSoon";
import LocationSwitcher from "./LocationSwitcher";
import OrganizationSwitcher from "./OrganizationSwitcher";
import { Mail } from "lucide-react";
import toast from "@/app/lib/toast";
import OnboardingChecklist from "./onboarding/OnboardingChecklist";
import OnboardingSupportBot from "./onboarding/OnboardingSupportBot";

interface DashboardLayoutProps {
  children: React.ReactNode;
  userData?: any;
}

type SystemMode = "crm" | "booking";

export default function DashboardLayout({
  children,
  userData,
}: DashboardLayoutProps) {
  const pathname = usePathname();
  const router = useRouter();
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [mounted, setMounted] = useState(false);
  const [user, setUser] = useState<any>(null);
  const [systemMode, setSystemMode] = useState<SystemMode>("crm");
  const [staffPermissions, setStaffPermissions] = useState<any>(null);
  const [organizationId, setOrganizationId] = useState<string | null>(null);
  const [unreadMessagesCount, setUnreadMessagesCount] = useState<number>(0);
  const supabase = createClient();

  useEffect(() => {
    setMounted(true);
    checkUser();
    loadSystemPreference();
    loadStaffPermissions();
    loadOrganization();
  }, []);

  useEffect(() => {
    if (!organizationId) return;

    // Initial fetch
    fetchUnreadMessagesCount(organizationId);

    // Subscribe to new inbound messages for this org
    const channel = supabase
      .channel(`messages-org-${organizationId}`)
      .on(
        "postgres_changes",
        {
          event: "INSERT",
          schema: "public",
          table: "messages",
          filter: `organization_id=eq.${organizationId}`,
        },
        async (payload: any) => {
          const newMsg = payload.new;
          if (newMsg?.direction === "inbound") {
            setUnreadMessagesCount((c) => c + 1);

            // Try to fetch sender name via lead
            try {
              if (newMsg.lead_id) {
                const { data: lead } = await supabase
                  .from("leads")
                  .select("first_name,last_name,phone,email")
                  .eq("id", newMsg.lead_id)
                  .single();
                const senderName =
                  [lead?.first_name, lead?.last_name]
                    .filter(Boolean)
                    .join(" ") ||
                  lead?.phone ||
                  lead?.email ||
                  "Unknown contact";
                const preview = (newMsg.body || newMsg.message || "")
                  .toString()
                  .slice(0, 80);
                toast.info(`New message from ${senderName}: ${preview}`);
              } else {
                const preview = (newMsg.body || newMsg.message || "")
                  .toString()
                  .slice(0, 80);
                toast.info(`New message: ${preview}`);
              }
            } catch (_) {
              const preview = (newMsg?.body || newMsg?.message || "")
                .toString()
                .slice(0, 80);
              toast.info(`New message: ${preview}`);
            }
          }
        },
      )
      // Recompute on updates that mark read
      .on(
        "postgres_changes",
        {
          event: "UPDATE",
          schema: "public",
          table: "messages",
          filter: `organization_id=eq.${organizationId}`,
        },
        () => {
          fetchUnreadMessagesCount(organizationId);
        },
      )
      .subscribe();

    return () => {
      supabase.removeChannel(channel);
    };
  }, [organizationId]);

  // Mark unread as read when on conversations page
  useEffect(() => {
    if (!organizationId) return;
    if (pathname?.startsWith("/conversations")) {
      markAllMessagesRead(organizationId);
    }
  }, [pathname, organizationId]);

  const checkUser = async () => {
    const {
      data: { user },
    } = await supabase.auth.getUser();
    setUser(user);
  };

  const loadSystemPreference = () => {
    const savedMode = localStorage.getItem("systemMode") as SystemMode;
    if (savedMode && (savedMode === "crm" || savedMode === "booking")) {
      setSystemMode(savedMode);
    }
  };

  const loadStaffPermissions = async () => {
    // Load staff permissions from database
    const {
      data: { user },
    } = await supabase.auth.getUser();
    if (user) {
      const { data } = await supabase
        .from("organization_staff")
        .select("permissions")
        .eq("user_id", user.id)
        .single();

      if (data) {
        setStaffPermissions(data.permissions);
      }
    }
  };

  const loadOrganization = async () => {
    const {
      data: { user },
    } = await supabase.auth.getUser();
    if (!user) return;
    const { data: userOrg } = await supabase
      .from("user_organizations")
      .select("organization_id")
      .eq("user_id", user.id)
      .single();
    if (userOrg?.organization_id) {
      setOrganizationId(userOrg.organization_id);
    }
  };

  const fetchUnreadMessagesCount = async (orgId: string) => {
    try {
      const { count } = await supabase
        .from("messages")
        .select("id", { count: "exact", head: true })
        .eq("organization_id", orgId)
        .eq("direction", "inbound")
        .is("read_at", null);
      setUnreadMessagesCount(count || 0);
    } catch (e) {
      // noop
    }
  };

  const markAllMessagesRead = async (orgId: string) => {
    try {
      await supabase
        .from("messages")
        .update({ read_at: new Date().toISOString(), status: "read" })
        .eq("organization_id", orgId)
        .eq("direction", "inbound")
        .is("read_at", null);
      setUnreadMessagesCount(0);
    } catch (e) {
      // noop
    }
  };

