  // Filter navigation based on staff permissions
  const getFilteredNavigation = (nav: any[]) => {
    if (!staffPermissions) return nav;

    return nav.filter((item) => {
      // Check if staff has permission to view this section
      if (
        staffPermissions[item.name.toLowerCase().replace(/\s+/g, "_")] === false
      ) {
        return false;
      }
      return true;
    });
  };

  const navigation =
    systemMode === "crm"
      ? getFilteredNavigation(crmNavigation)
      : getFilteredNavigation(bookingNavigation);

  const trialEnds = userData?.trialEnds ? new Date(userData.trialEnds) : null;
  const daysLeft =
    mounted && trialEnds
      ? Math.ceil(
          (trialEnds.getTime() - new Date().getTime()) / (1000 * 60 * 60 * 24),
        )
      : 0;

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      {/* Sidebar */}
      <div
        className={`${sidebarOpen ? "w-64" : "w-16"} bg-gray-800 transition-all duration-300 flex flex-col fixed left-0 top-0 h-screen overflow-y-auto`}
      >
        {/* Logo and System Toggle */}
        <div className="p-4 border-b border-gray-700">
          <div className="flex items-center justify-between mb-4">
            <h1
              className={`${sidebarOpen ? "block" : "hidden"} text-xl font-bold text-orange-500`}
            >
              Gymleadhub
            </h1>
            <button
              onClick={() => setSidebarOpen(!sidebarOpen)}
              className="text-gray-400 hover:text-white"
            >
              <svg
                className="w-6 h-6"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d={
                    sidebarOpen
                      ? "M6 18L18 6M6 6l12 12"
                      : "M4 6h16M4 12h16M4 18h16"
                  }
                />
              </svg>
            </button>
          </div>

          {/* System Mode Toggle */}
          <div
            className={`${sidebarOpen ? "block" : "hidden"} bg-gray-700 rounded-lg p-1`}
          >
            <div className="grid grid-cols-2 gap-1">
              <button
                onClick={() => handleSystemModeChange("crm")}
                className={`px-3 py-2 rounded-md text-sm font-medium transition-colors ${
                  systemMode === "crm"
                    ? "bg-blue-600 text-white"
                    : "text-gray-300 hover:text-white hover:bg-gray-600"
                }`}
              >
                CRM
              </button>
              <button
                onClick={() => handleSystemModeChange("booking")}
                className={`px-3 py-2 rounded-md text-sm font-medium transition-colors ${
                  systemMode === "booking"
                    ? "bg-green-600 text-white"
                    : "text-gray-300 hover:text-white hover:bg-gray-600"
                }`}
              >
                Booking
              </button>
            </div>
          </div>
        </div>

        {/* Navigation */}
        <nav className="flex-1 px-2 py-4 space-y-1 overflow-y-auto">
          {navigation.map((item) => {
            const isActive =
              pathname === item.href || pathname.startsWith(item.href + "/");
            return (
              <Link
                key={item.name}
                href={item.href}
                onClick={() => {
                  console.log(`Navigating from ${pathname} to ${item.href}`);
                }}
                className={`
                  flex items-center px-2 py-2 text-sm font-medium rounded-md transition-colors
                  ${
                    isActive
                      ? systemMode === "crm"
                        ? "bg-blue-600 text-white"
                        : "bg-green-600 text-white"
                      : "text-gray-300 hover:bg-gray-700 hover:text-white"
                  }
                `}
              >
                <div className="mr-3 flex-shrink-0">{item.icon}</div>
                <span
                  className={`${sidebarOpen ? "block" : "hidden"} flex items-center`}
                >
                  {item.name}
                  {item.showBadge && sidebarOpen && <ComingSoonBadge />}
                </span>
              </Link>
            );
          })}
        </nav>

        {/* User Menu & Trial Info */}
        <div className="p-4 border-t border-gray-700">
          {mounted && trialEnds && daysLeft > 0 && (
            <div
              className={`${sidebarOpen ? "block" : "hidden"} mb-4 p-3 bg-orange-900 bg-opacity-50 rounded-lg`}
            >
              <p className="text-sm text-orange-300">
                Trial ends in {daysLeft} days
              </p>
              <Link
                href="/billing"
                className="text-xs text-orange-400 hover:text-orange-300 underline"
              >
                Upgrade now
              </Link>
            </div>
          )}
          <Link
            href="/settings"
            className={`w-full flex items-center px-2 py-2 mb-2 text-sm font-medium rounded-md text-gray-300 hover:bg-gray-700 hover:text-white transition-colors`}
          >
            <svg
              className="mr-3 w-5 h-5"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M10.325 4.317c.426-1.756 2.924-1.756 3.35 0a1.724 1.724 0 002.573 1.066c1.543-.94 3.31.826 2.37 2.37a1.724 1.724 0 001.065 2.572c1.756.426 1.756 2.924 0 3.35a1.724 1.724 0 00-1.066 2.573c.94 1.543-.826 3.31-2.37 2.37a1.724 1.724 0 00-2.572 1.065c-.426 1.756-2.924 1.756-3.35 0a1.724 1.724 0 00-2.573-1.066c-1.543.94-3.31-.826-2.37-2.37a1.724 1.724 0 00-1.065-2.572c-1.756-.426-1.756-2.924 0-3.35a1.724 1.724 0 001.066-2.573c-.94-1.543.826-3.31 2.37-2.37.996.608 2.296.07 2.572-1.065z"
              />
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M15 12a3 3 0 11-6 0 3 3 0 016 0z"
              />
            </svg>
            <span className={`${sidebarOpen ? "block" : "hidden"}`}>
              Settings
            </span>
          </Link>
          <button
            onClick={handleLogout}
            className={`w-full flex items-center px-2 py-2 text-sm font-medium rounded-md text-gray-300 hover:bg-gray-700 hover:text-white transition-colors`}
          >
            <svg
              className="mr-3 w-5 h-5"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M17 16l4-4m0 0l-4-4m4 4H7m6 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h4a3 3 0 013 3v1"
              />
            </svg>
            <span className={`${sidebarOpen ? "block" : "hidden"}`}>
              Logout
            </span>
          </button>
        </div>
      </div>

      {/* Main content */}
      <div
        className={`${sidebarOpen ? "ml-64" : "ml-16"} flex flex-col transition-all duration-300`}
      >
        {/* Top bar with system indicator */}
        <div className="bg-gray-800 border-b border-gray-700 px-6 py-3">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <h2 className="text-lg font-medium">
                {systemMode === "crm" ? (
                  <span className="flex items-center">
                    <span className="w-3 h-3 bg-blue-500 rounded-full mr-2"></span>
                    CRM System
                  </span>
                ) : (
                  <span className="flex items-center">
                    <span className="w-3 h-3 bg-green-500 rounded-full mr-2"></span>
                    Booking System
                  </span>
                )}
              </h2>
              <span className="text-sm text-gray-400">
                {systemMode === "crm"
                  ? "Customer relationship management"
                  : "Class booking and scheduling"}
              </span>
            </div>
            <div className="flex items-center space-x-4">
              <OrganizationSwitcher />
              <LocationSwitcher />
              <button
                type="button"
                aria-label="Messages"
                onClick={() => router.push("/conversations")}
                className="relative p-2 rounded-md hover:bg-gray-700 transition-colors"
              >
                <Mail
                  className={`h-5 w-5 ${unreadMessagesCount > 0 ? "text-red-500" : "text-gray-400"}`}
                />
                {unreadMessagesCount > 0 && (
                  <span
                    className="absolute -top-1 -right-1 bg-red-500 text-white text-xs rounded-full px-1.5 py-0.5 min-w-[1.25rem] text-center"
                    data-testid="messages-unread-badge"
                  >
                    {unreadMessagesCount}
                  </span>
                )}
              </button>
              {user && (
                <span className="text-sm text-gray-400">{user.email}</span>
              )}
            </div>
          </div>
        </div>

        {/* Page content */}
        <main className="flex-1 overflow-y-auto">{children}</main>
      </div>

      {/* Onboarding Components */}
      <OnboardingChecklist />
      <OnboardingSupportBot />
    </div>
  );
}

