"use client";

import { useState, useEffect } from "react";
import { useParams, useRouter } from "next/navigation";
import { createClient } from "@/app/lib/supabase/client";
import { formatBritishCurrency } from "@/app/lib/utils/british-format";
import { ChevronLeft, Edit2, Pause, X, ChevronDown } from "lucide-react";

interface MembershipDetails {
  id: string;
  client_id: string;
  membership_plan_id: string;
  status: string;
  start_date: string;
  end_date: string | null;
  billing_interval: string;
  next_billing_date: string | null;
  notes: string | null;
  created_at: string;
  membership_plan: {
    name: string;
    description: string;
    price: number;
    price_pennies: number;
    billing_period: string;
  };
  client: {
    first_name: string;
    last_name: string;
    email: string;
  };
}

interface Payment {
  id: string;
  amount: number;
  payment_date: string;
  payment_status: string;
  payment_provider: string;
  description: string;
}

type Tab = "details" | "past_payments" | "upcoming_payments" | "skipped_payments" | "usage";

export default function MembershipDetailPage() {
  const params = useParams();
  const router = useRouter();
  const customerId = params.customerId as string;
  const membershipId = params.membershipId as string;

  const [membership, setMembership] = useState<MembershipDetails | null>(null);
  const [pastPayments, setPastPayments] = useState<Payment[]>([]);
  const [upcomingPayments, setUpcomingPayments] = useState<Payment[]>([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState<Tab>("details");

  useEffect(() => {
    fetchMembershipDetails();
    fetchPayments();
  }, [membershipId]);

  const fetchMembershipDetails = async () => {
    try {
      const supabase = createClient();
      const { data, error } = await supabase
        .from("customer_memberships")
        .select(`
          *,
          membership_plan:membership_plans(*),
          client:clients(first_name, last_name, email)
        `)
        .eq("id", membershipId)
        .single();

      if (error) throw error;
      setMembership(data);
    } catch (error) {
      console.error("Error fetching membership:", error);
    }
  };

  const fetchPayments = async () => {
    try {
      const supabase = createClient();

      // Fetch past payments (completed)
      const { data: past } = await supabase
        .from("payments")
        .select("*")
        .eq("client_id", customerId)
        .in("payment_status", ["paid_out", "succeeded"])
        .order("payment_date", { ascending: false });

      setPastPayments(past || []);

      // Fetch upcoming payments (pending/scheduled)
      const { data: upcoming } = await supabase
        .from("payments")
        .select("*")
        .eq("client_id", customerId)
        .in("payment_status", ["pending", "outstanding"])
        .order("payment_date", { ascending: true });

      setUpcomingPayments(upcoming || []);
    } catch (error) {
      console.error("Error fetching payments:", error);
    } finally {
      setLoading(false);
    }
  };

  const getStatusBadge = (status: string) => {
    const styles: Record<string, string> = {
      active: "bg-green-600",
      paused: "bg-yellow-600",
      cancelled: "bg-red-600",
      expired: "bg-gray-600",
    };
    return (
      <span className={`px-3 py-1 rounded-full text-xs font-medium ${styles[status] || styles.expired}`}>
        {status.charAt(0).toUpperCase() + status.slice(1)}
      </span>
    );
  };

  const getPaymentStatusBadge = (status: string) => {
    const styles: Record<string, string> = {
      paid_out: "bg-green-600 text-white",
      succeeded: "bg-green-600 text-white",
      pending: "bg-yellow-600 text-white",
      outstanding: "bg-orange-600 text-white",
      failed: "bg-red-600 text-white",
    };
    return (
      <span className={`px-2 py-1 rounded text-xs font-medium ${styles[status] || "bg-gray-600 text-white"}`}>
        {status.charAt(0).toUpperCase() + status.slice(1).replace("_", " ")}
      </span>
    );
  };

  if (loading || !membership) {
    return (
      <div className="min-h-screen bg-gray-900 text-white flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto"></div>
          <p className="mt-4 text-gray-400">Loading membership details...</p>
        </div>
      </div>
    );
  }

  const priceInPennies = membership.membership_plan.price_pennies || membership.membership_plan.price * 100;

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      <div className="max-w-6xl mx-auto p-6">
        {/* Back Button */}
        <button
          onClick={() => router.push(`/members/${customerId}`)}
          className="flex items-center gap-2 text-gray-400 hover:text-white mb-6"
        >
          <ChevronLeft className="h-5 w-5" />
          Back to Member Profile
        </button>

        {/* Membership Summary Header */}
        <div className="bg-gray-800 rounded-lg p-6 mb-6">
          <div className="flex items-start justify-between mb-4">
            <div>
              <h1 className="text-2xl font-bold mb-2">
                {membership.membership_plan.name}
              </h1>
              <p className="text-gray-400">
                {membership.client.first_name} {membership.client.last_name}
              </p>
            </div>
            {getStatusBadge(membership.status)}
          </div>

          <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-6">
            <div>
              <p className="text-sm text-gray-400">Start Date</p>
              <p className="font-medium">{new Date(membership.start_date).toLocaleDateString("en-GB")}</p>
            </div>
            <div>
              <p className="text-sm text-gray-400">End Date</p>
              <p className="font-medium">
                {membership.end_date
                  ? new Date(membership.end_date).toLocaleDateString("en-GB")
                  : "Never Expires"}
              </p>
            </div>
            <div>
              <p className="text-sm text-gray-400">Billing</p>
              <p className="font-medium">
                {formatBritishCurrency(priceInPennies, true)}/{membership.membership_plan.billing_period}
              </p>
            </div>
            <div>
              <p className="text-sm text-gray-400">Next Payment</p>
              <p className="font-medium">
                {membership.next_billing_date
                  ? new Date(membership.next_billing_date).toLocaleDateString("en-GB")
                  : "N/A"}
              </p>
            </div>
          </div>

          {/* Action Buttons */}
          <div className="flex gap-3">
            <button className="flex items-center gap-2 px-4 py-2 bg-blue-600 hover:bg-blue-700 rounded-lg">
              <Edit2 className="h-4 w-4" />
              Edit
            </button>
            <button className="flex items-center gap-2 px-4 py-2 bg-yellow-600 hover:bg-yellow-700 rounded-lg">
              <Pause className="h-4 w-4" />
              Hold
            </button>
            <button className="flex items-center gap-2 px-4 py-2 bg-red-600 hover:bg-red-700 rounded-lg">
              <X className="h-4 w-4" />
              Cancel
            </button>
            <div className="ml-auto flex gap-3">
              <button className="flex items-center gap-2 px-4 py-2 bg-gray-700 hover:bg-gray-600 rounded-lg">
                Upgrades
                <ChevronDown className="h-4 w-4" />
              </button>
              <button className="flex items-center gap-2 px-4 py-2 bg-gray-700 hover:bg-gray-600 rounded-lg">
                Downgrades
                <ChevronDown className="h-4 w-4" />
              </button>
            </div>
          </div>
        </div>

        {/* Tabs */}
        <div className="bg-gray-800 rounded-lg">
          <div className="border-b border-gray-700">
            <div className="flex gap-8 px-6">
              {[
                { id: "details", label: "Details" },
                { id: "past_payments", label: "Past Payments" },
                { id: "upcoming_payments", label: "Upcoming Payments" },
                { id: "skipped_payments", label: "Skipped Payments" },
                { id: "usage", label: "Usage" },
              ].map((tab) => (
                <button
                  key={tab.id}
                  onClick={() => setActiveTab(tab.id as Tab)}
                  className={`py-4 px-2 border-b-2 transition-colors ${
                    activeTab === tab.id
                      ? "border-blue-500 text-blue-500"
                      : "border-transparent text-gray-400 hover:text-white"
                  }`}
                >
                  {tab.label}
                </button>
              ))}
            </div>
          </div>

          <div className="p-6">
            {activeTab === "details" && (
              <div className="space-y-4">
                <div>
                  <h3 className="text-sm font-medium text-gray-400 mb-1">Description</h3>
                  <p className="text-white">
                    {membership.membership_plan.description || "No description available"}
                  </p>
                </div>
                <div>
                  <h3 className="text-sm font-medium text-gray-400 mb-1">Notes</h3>
                  <p className="text-white">{membership.notes || "No notes"}</p>
                </div>
                <div>
                  <h3 className="text-sm font-medium text-gray-400 mb-1">Created</h3>
                  <p className="text-white">
                    {new Date(membership.created_at).toLocaleString("en-GB")}
                  </p>
                </div>
              </div>
            )}

            {activeTab === "past_payments" && (
              <div className="overflow-x-auto">
                <table className="w-full">
                  <thead>
                    <tr className="border-b border-gray-700">
                      <th className="text-left py-3 px-4 text-sm font-medium text-gray-400">Date</th>
                      <th className="text-left py-3 px-4 text-sm font-medium text-gray-400">Amount</th>
                      <th className="text-left py-3 px-4 text-sm font-medium text-gray-400">Status</th>
                      <th className="text-left py-3 px-4 text-sm font-medium text-gray-400">Provider</th>
                      <th className="text-left py-3 px-4 text-sm font-medium text-gray-400">Description</th>
                    </tr>
                  </thead>
                  <tbody>
                    {pastPayments.length === 0 ? (
                      <tr>
                        <td colSpan={5} className="text-center py-8 text-gray-400">
                          No past payments recorded
                        </td>
                      </tr>
                    ) : (
                      pastPayments.map((payment) => (
                        <tr key={payment.id} className="border-b border-gray-700">
                          <td className="py-3 px-4">
                            {new Date(payment.payment_date).toLocaleDateString("en-GB")}
                          </td>
                          <td className="py-3 px-4">
                            {formatBritishCurrency(payment.amount, true)}
                          </td>
                          <td className="py-3 px-4">
                            {getPaymentStatusBadge(payment.payment_status)}
                          </td>
                          <td className="py-3 px-4 capitalize">{payment.payment_provider}</td>
                          <td className="py-3 px-4 text-gray-400">
                            {payment.description || "â€”"}
                          </td>
                        </tr>
                      ))
                    )}
                  </tbody>
                </table>
              </div>
            )}

            {activeTab === "upcoming_payments" && (
              <div className="overflow-x-auto">
                <table className="w-full">
                  <thead>
                    <tr className="border-b border-gray-700">
                      <th className="text-left py-3 px-4 text-sm font-medium text-gray-400">Due Date</th>
                      <th className="text-left py-3 px-4 text-sm font-medium text-gray-400">Amount</th>
                      <th className="text-left py-3 px-4 text-sm font-medium text-gray-400">Status</th>
                      <th className="text-left py-3 px-4 text-sm font-medium text-gray-400">Provider</th>
                    </tr>
                  </thead>
                  <tbody>
                    {upcomingPayments.length === 0 ? (
                      <tr>
                        <td colSpan={4} className="text-center py-8 text-gray-400">
                          No upcoming payments scheduled
                        </td>
                      </tr>
                    ) : (
                      upcomingPayments.map((payment) => (
                        <tr key={payment.id} className="border-b border-gray-700">
                          <td className="py-3 px-4">
                            {new Date(payment.payment_date).toLocaleDateString("en-GB")}
                          </td>
                          <td className="py-3 px-4">
                            {formatBritishCurrency(payment.amount, true)}
                          </td>
                          <td className="py-3 px-4">
                            {getPaymentStatusBadge(payment.payment_status)}
                          </td>
                          <td className="py-3 px-4 capitalize">{payment.payment_provider}</td>
                        </tr>
                      ))
                    )}
                  </tbody>
                </table>
              </div>
            )}

            {activeTab === "skipped_payments" && (
              <div className="text-center py-8 text-gray-400">
                No skipped payments
              </div>
            )}

            {activeTab === "usage" && (
              <div className="space-y-4">
                <div>
                  <h3 className="text-sm font-medium text-gray-400 mb-2">Membership Usage</h3>
                  <p className="text-white">
                    {membership.end_date
                      ? `${Math.ceil((new Date(membership.end_date).getTime() - new Date().getTime()) / (1000 * 60 * 60 * 24))} days remaining`
                      : "Never Expires"}
                  </p>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

