   788â†’ */
   789â†’export class SendBulkMessageTool extends BaseTool {
   790â†’  id = 'send_bulk_message';
   791â†’  name = 'Send Bulk Message';
   792â†’  description = 'Send the same message to multiple clients or leads (max 100 recipients per request)';
   793â†’  category = 'messaging' as const;
   794â†’
   795â†’  parametersSchema = z.object({
   796â†’    recipients: z.array(z.object({
   797â†’      id: z.string().uuid(),
   798â†’      type: z.enum(['client', 'lead']),
   799â†’    })).max(100).describe('List of recipients (max 100)'),
   800â†’    channel: z.enum(['sms', 'email', 'whatsapp']).describe('Communication channel'),
   801â†’    message: z.string().min(1).describe('Message to send'),
   802â†’    subject: z.string().optional().describe('Email subject (required for email)'),
   803â†’    personalizeWithName: z.boolean().default(true).describe('Replace {name} with recipient name'),
   804â†’  });
   805â†’
   806â†’  requiresPermission = 'messages:bulk_send';
   807â†’
   808â†’  async execute(params: any, context: ToolExecutionContext): Promise<ToolExecutionResult> {
   809â†’    const startTime = Date.now();
   810â†’    const validated = this.parametersSchema.parse(params);
   811â†’
   812â†’    try {
   813â†’      const supabase = createAdminClient();
   814â†’
   815â†’      let sent = 0;
   816â†’      let failed = 0;
   817â†’
   818â†’      // Process recipients
   819â†’      for (const recipient of validated.recipients) {
   820â†’        try {
   821â†’          const table = recipient.type === 'client' ? 'clients' : 'leads';
   822â†’          const { data: entity } = await supabase
   823â†’            .from(table)
   824â†’            .select('id, name, email, phone')
   825â†’            .eq('id', recipient.id)
   826â†’            .eq('organization_id', context.organizationId)
   827â†’            .single();
   828â†’
   829â†’          if (!entity) {
   830â†’            failed++;
   831â†’            continue;
   832â†’          }
   833â†’
   834â†’          const personalizedMessage = validated.personalizeWithName
   835â†’            ? validated.message.replace(/{name}/g, entity.name)
   836â†’            : validated.message;
   837â†’
   838â†’          // Log message
   839â†’          await supabase.from('messages').insert({
   840â†’            organization_id: context.organizationId,
   841â†’            client_id: recipient.type === 'client' ? entity.id : null,
   842â†’            lead_id: recipient.type === 'lead' ? entity.id : null,
   843â†’            type: validated.channel,
   844â†’            channel: validated.channel,
   845â†’            direction: 'outbound',
   846â†’            status: 'sent',
   847â†’            subject: validated.subject,
   848â†’            body: personalizedMessage,
   849â†’            content: personalizedMessage,
   850â†’            sender_type: 'ai',
   851â†’            sender_name: 'Bulk Campaign',
   852â†’          });
   853â†’
   854â†’          sent++;
   855â†’
   856â†’        } catch (err) {
   857â†’          failed++;
   858â†’        }
   859â†’      }
   860â†’
   861â†’      return {
   862â†’        success: true,
   863â†’        data: {
   864â†’          total: validated.recipients.length,
   865â†’          sent,
   866â†’          failed,
   867â†’          channel: validated.channel,
   868â†’        },
   869â†’        metadata: {
   870â†’          executionTimeMs: Date.now() - startTime,
   871â†’        },
   872â†’      };
   873â†’
   874â†’    } catch (error: any) {
   875â†’      return {
   876â†’        success: false,
   877â†’        error: error.message,
   878â†’        metadata: {
   879â†’          executionTimeMs: Date.now() - startTime,
   880â†’        },
   881â†’      };
   882â†’    }
   883â†’  }
   884â†’}
