                </table>
              </div>
            )}

            {activeTab === "upcoming_payments" && (
              <div className="overflow-x-auto">
                <table className="w-full">
                  <thead>
                    <tr className="border-b border-gray-700">
                      <th className="text-left py-3 px-4 text-sm font-medium text-gray-400">Due Date</th>
                      <th className="text-left py-3 px-4 text-sm font-medium text-gray-400">Amount</th>
                      <th className="text-left py-3 px-4 text-sm font-medium text-gray-400">Status</th>
                      <th className="text-left py-3 px-4 text-sm font-medium text-gray-400">Provider</th>
                    </tr>
                  </thead>
                  <tbody>
                    {upcomingPayments.length === 0 ? (
                      <tr>
                        <td colSpan={4} className="text-center py-8 text-gray-400">
                          No upcoming payments scheduled
                        </td>
                      </tr>
                    ) : (
                      upcomingPayments.map((payment) => (
                        <tr key={payment.id} className="border-b border-gray-700">
                          <td className="py-3 px-4">
                            {new Date(payment.payment_date).toLocaleDateString("en-GB")}
                          </td>
                          <td className="py-3 px-4">
                            {formatBritishCurrency(payment.amount, true)}
                          </td>
                          <td className="py-3 px-4">
                            {getPaymentStatusBadge(payment.payment_status)}
                          </td>
                          <td className="py-3 px-4 capitalize">{payment.payment_provider}</td>
                        </tr>
                      ))
                    )}
                  </tbody>
                </table>
              </div>
            )}

            {activeTab === "skipped_payments" && (
              <div className="text-center py-8 text-gray-400">
                No skipped payments
              </div>
            )}

            {activeTab === "usage" && (
              <div className="space-y-4">
                <div>
                  <h3 className="text-sm font-medium text-gray-400 mb-2">Membership Usage</h3>
                  <p className="text-white">
                    {membership.end_date
                      ? `${Math.ceil((new Date(membership.end_date).getTime() - new Date().getTime()) / (1000 * 60 * 60 * 24))} days remaining`
                      : "Never Expires"}
                  </p>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

