    47→      let { data, error } = await supabase
    48→        .from("clients")
    49→        .select("first_name, last_name")
    50→        .eq("id", customerId)
    51→        .single();
    52→
    53→      if (!error && data) {
    54→        const name = `${data.first_name || ""} ${data.last_name || ""}`.trim();
    55→        setCustomerName(name || "Unknown Member");
    56→        return;
    57→      }
    58→
    59→      // Fallback to leads table
    60→      const leadResult = await supabase
    61→        .from("leads")
    62→        .select("name, first_name, last_name")
    63→        .eq("id", customerId)
    64→        .single();
    65→
    66→      if (!leadResult.error && leadResult.data) {
    67→        const name =
    68→          leadResult.data.name ||
    69→          `${leadResult.data.first_name || ""} ${leadResult.data.last_name || ""}`.trim();
    70→        setCustomerName(name || "Unknown Member");
    71→      }
    72→    } catch (error) {
    73→      console.error("Error fetching customer name:", error);
    74→      setCustomerName("Unknown Member");
    75→    }
    76→  };
    77→
    78→  const fetchMemberships = async () => {
    79→    try {
    80→      setLoading(true);
    81→      const response = await fetch(
    82→        `/api/customer-memberships?customerId=${customerId}`,
    83→      );
    84→
    85→      if (!response.ok) {
    86→        const error = await response.json();
    87→        console.error("Error fetching memberships:", error);
    88→        setMemberships([]);
