   455→function EditMembershipModal({
   456→  membership,
   457→  onClose,
   458→  onSaved,
   459→}: {
   460→  membership: any;
   461→  onClose: () => void;
   462→  onSaved: () => void;
   463→}) {
   464→  const supabase = createClient();
   465→  const [endDate, setEndDate] = useState<string>(membership.end_date || "");
   466→  const [nextBilling, setNextBilling] = useState<string>(
   467→    membership.next_billing_date || "",
   468→  );
   469→  const [notes, setNotes] = useState<string>(membership.notes || "");
   470→  const [saving, setSaving] = useState(false);
   471→  const [error, setError] = useState("");
   472→
   473→  const handleSave = async (e: React.FormEvent) => {
   474→    e.preventDefault();
   475→    setSaving(true);
   476→    setError("");
   477→    try {
   478→      const updates: any = {
   479→        end_date: endDate || null,
   480→        next_billing_date: nextBilling || null,
   481→        notes: notes || null,
   482→      };
   483→      const { error } = await supabase
   484→        .from("customer_memberships")
   485→        .update(updates)
   486→        .eq("id", membership.id);
   487→      if (error) throw error;
   488→      await onSaved();
   489→    } catch (err: any) {
   490→      setError(err.message || "Failed to save");
   491→    } finally {
   492→      setSaving(false);
   493→    }
   494→  };
   495→
   496→  return (
   497→    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
   498→      <div className="bg-gray-800 rounded-lg p-6 w-full max-w-md">
   499→        <div className="flex items-center justify-between mb-4">
   500→          <h2 className="text-xl font-semibold text-white">Edit Membership</h2>
   501→          <button onClick={onClose} className="text-gray-400 hover:text-white">
   502→            ✕
   503→          </button>
   504→        </div>
   505→        <form onSubmit={handleSave} className="space-y-4">
   506→          <div>
   507→            <label className="block text-sm font-medium text-gray-300 mb-2">
   508→              End Date
   509→            </label>
   510→            <input
   511→              type="date"
   512→              value={endDate || ""}
   513→              onChange={(e) => setEndDate(e.target.value)}
   514→              className="w-full bg-gray-700 text-white rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
   515→            />
   516→          </div>
   517→          <div>
   518→            <label className="block text-sm font-medium text-gray-300 mb-2">
   519→              Next Billing Date
   520→            </label>
   521→            <input
   522→              type="date"
   523→              value={nextBilling || ""}
   524→              onChange={(e) => setNextBilling(e.target.value)}
   525→              className="w-full bg-gray-700 text-white rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
   526→            />
   527→          </div>
   528→          <div>
   529→            <label className="block text-sm font-medium text-gray-300 mb-2">
   530→              Notes
   531→            </label>
   532→            <textarea
   533→              value={notes}
   534→              onChange={(e) => setNotes(e.target.value)}
   535→              rows={3}
   536→              className="w-full bg-gray-700 text-white rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
   537→            />
   538→          </div>
   539→          {error && (
   540→            <div className="bg-red-600 bg-opacity-20 border border-red-600 rounded-lg p-3">
   541→              <p className="text-red-400 text-sm">{error}</p>
   542→            </div>
   543→          )}
   544→          <div className="flex gap-3">
   545→            <button
   546→              type="button"
   547→              onClick={onClose}
   548→              className="flex-1 px-4 py-2 bg-gray-700 text-white rounded-lg hover:bg-gray-600"
   549→              disabled={saving}
   550→            >
   551→              Cancel
   552→            </button>
   553→            <button
   554→              type="submit"
   555→              className="flex-1 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50"
   556→              disabled={saving}
   557→            >
   558→              {saving ? "Saving..." : "Save Changes"}
   559→            </button>
   560→          </div>
   561→        </form>
   562→      </div>
   563→    </div>
   564→  );
   565→}
   566→
   567→function UpgradeMembershipModal({
   568→  membership,
   569→  plans,
   570→  onClose,
   571→  onSaved,
   572→}: {
   573→  membership: any;
   574→  plans: any[];
   575→  onClose: () => void;
   576→  onSaved: () => void;
   577→}) {
   578→  const supabase = createClient();
   579→  const [planId, setPlanId] = useState<string>(
   580→    membership.membership_plan_id || "",
   581→  );
   582→  const [effectiveDate, setEffectiveDate] = useState<string>(
   583→    new Date().toISOString().split("T")[0],
   584→  );
   585→  const [saving, setSaving] = useState(false);
   586→  const [error, setError] = useState("");
   587→
   588→  const handleUpgrade = async (e: React.FormEvent) => {
   589→    e.preventDefault();
   590→    if (!planId) {
   591→      setError("Please select a plan");
   592→      return;
   593→    }
   594→    setSaving(true);
   595→    setError("");
   596→    try {
   597→      const updates: any = {
   598→        membership_plan_id: planId,
   599→        start_date: effectiveDate,
   600→      };
   601→      const { error } = await supabase
   602→        .from("customer_memberships")
   603→        .update(updates)
   604→        .eq("id", membership.id);
   605→      if (error) throw error;
   606→      await onSaved();
   607→    } catch (err: any) {
   608→      setError(err.message || "Failed to upgrade");
   609→    } finally {
   610→      setSaving(false);
   611→    }
   612→  };
   613→
   614→  return (
   615→    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
   616→      <div className="bg-gray-800 rounded-lg p-6 w-full max-w-md">
   617→        <div className="flex items-center justify-between mb-4">
   618→          <h2 className="text-xl font-semibold text-white">
   619→            Upgrade Membership
   620→          </h2>
   621→          <button onClick={onClose} className="text-gray-400 hover:text-white">
   622→            ✕
   623→          </button>
   624→        </div>
   625→        <form onSubmit={handleUpgrade} className="space-y-4">
   626→          <div>
   627→            <label className="block text-sm font-medium text-gray-300 mb-2">
   628→              New Plan *
   629→            </label>
   630→            <select
   631→              value={planId}
   632→              onChange={(e) => setPlanId(e.target.value)}
   633→              className="w-full bg-gray-700 text-white rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
   634→              required
   635→            >
   636→              <option value="">Select a plan</option>
   637→              {plans.map((plan) => (
   638→                <option key={plan.id} value={plan.id}>
   639→                  {plan.name} - {formatBritishCurrency(plan.price || 0)}/
   640→                  {plan.billing_period || "month"}
   641→                </option>
   642→              ))}
   643→            </select>
   644→          </div>
   645→          <div>
   646→            <label className="block text-sm font-medium text-gray-300 mb-2">
   647→              Effective From *
   648→            </label>
   649→            <input
   650→              type="date"
