"use client";

import { useState, useEffect } from "react";
import { formatBritishCurrency } from "@/app/lib/utils/british-format";
import AddMembershipModal from "../AddMembershipModal";
import { createClient } from "@/app/lib/supabase/client";

interface MembershipsTabProps {
  customerId: string;
}

export default function MembershipsTab({ customerId }: MembershipsTabProps) {
  const [memberships, setMemberships] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const [showAddModal, setShowAddModal] = useState(false);
  const [customerName, setCustomerName] = useState("");

  useEffect(() => {
    fetchMemberships();
    fetchCustomerName();
  }, [customerId]);

  const fetchCustomerName = async () => {
    try {
      const supabase = createClient();
      const { data } = await supabase
        .from("clients")
        .select("first_name, last_name")
        .eq("id", customerId)
        .single();

      if (data) {
        const name = `${data.first_name || ""} ${data.last_name || ""}`.trim();
        setCustomerName(name || "Unknown Member");
      }
    } catch (error) {
      console.error("Error fetching customer name:", error);
      setCustomerName("Unknown Member");
    }
  };

  const fetchMemberships = async () => {
    try {
      setLoading(true);
      const response = await fetch(`/api/customer-memberships?customerId=${customerId}`);

      if (!response.ok) {
        setMemberships([]);
        return;
      }

      const result = await response.json();
      setMemberships(result.memberships || []);
    } catch (error) {
      console.error("Error fetching memberships:", error);
      setMemberships([]);
    } finally {
      setLoading(false);
    }
  };

  const getUsageText = (membership: any) => {
    if (!membership.end_date) return "Never Expires.";

    const endDate = new Date(membership.end_date);
    const today = new Date();
    const diffTime = endDate.getTime() - today.getTime();
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

    if (diffDays < 0) return "Expired";
    if (diffDays === 0) return "Expires today";
    if (diffDays === 1) return "1 day left";
    return `${diffDays} days left`;
  };

