     1→/**
     2→ * AI Agent Tool Registry System
     3→ *
     4→ * Central registry for all AI agent tools with support for:
     5→ * - Tool registration and discovery
     6→ * - OpenAI and Anthropic format conversion
     7→ * - Tool execution with context
     8→ * - Database synchronization
     9→ */
    10→
    11→import { AgentTool, ToolExecutionContext, ToolExecutionResult } from './types';
    12→import { createAdminClient } from '../../../app/lib/supabase/admin';
    13→
    14→// Import all tool categories
    15→import { DATA_TOOLS } from './data-tools';
    16→import { ANALYTICS_TOOLS } from './analytics-tools';
    17→import { MESSAGING_TOOLS } from './messaging-tools';
    18→import { AUTOMATION_TOOLS } from './automation-tools';
    19→
    20→/**
    21→ * Tool Registry Class
    22→ * Manages all available tools and provides conversion utilities
    23→ */
    24→export class ToolRegistry {
    25→  private tools: Map<string, AgentTool> = new Map();
    26→
    27→  constructor() {
    28→    // Auto-register all tools on instantiation
    29→    this.registerAllTools();
    30→  }
    31→
    32→  /**
    33→   * Register all tools from all categories
    34→   */
    35→  private registerAllTools(): void {
    36→    const allTools = [
    37→      ...DATA_TOOLS,
    38→      ...ANALYTICS_TOOLS,
    39→      ...MESSAGING_TOOLS,
    40→      ...AUTOMATION_TOOLS
    41→    ];
    42→
    43→    allTools.forEach(tool => this.registerTool(tool));
    44→  }
    45→
    46→  /**
    47→   * Register a single tool
    48→   */
    49→  registerTool(tool: AgentTool): void {
    50→    if (this.tools.has(tool.id)) {
    51→      console.warn(`Tool ${tool.id} is already registered. Overwriting.`);
    52→    }
    53→    this.tools.set(tool.id, tool);
    54→  }
    55→
    56→  /**
    57→   * Get a specific tool by ID
    58→   */
    59→  getTool(id: string): AgentTool | undefined {
    60→    return this.tools.get(id);
    61→  }
    62→
    63→  /**
    64→   * Get all registered tools
    65→   */
    66→  getAllTools(): AgentTool[] {
    67→    return Array.from(this.tools.values());
    68→  }
    69→
    70→  /**
    71→   * Get tools filtered by category
    72→   */
    73→  getToolsByCategory(category: string): AgentTool[] {
    74→    return this.getAllTools().filter(tool => tool.category === category);
    75→  }
    76→
    77→  /**
    78→   * Get enabled tools only
    79→   */
    80→  getEnabledTools(): AgentTool[] {
    81→    return this.getAllTools().filter(tool => tool.enabled);
    82→  }
    83→
    84→  /**
    85→   * Get tools by IDs and convert to OpenAI function format
    86→   */
    87→  getToolsForOpenAI(toolIds: string[]): Array<{
    88→    type: 'function';
    89→    function: {
    90→      name: string;
    91→      description: string;
    92→      parameters: {
    93→        type: 'object';
    94→        properties: Record<string, any>;
    95→        required: string[];
    96→      };
    97→    };
    98→  }> {
    99→    return toolIds
   100→      .map(id => this.getTool(id))
   101→      .filter((tool): tool is AgentTool => tool !== undefined && tool.enabled)
   102→      .map(tool => ({
   103→        type: 'function' as const,
   104→        function: tool.toOpenAIFunction()
   105→      }));
   106→  }
   107→
   108→  /**
   109→   * Get tools by IDs and convert to Anthropic tool format
   110→   */
   111→  getToolsForAnthropic(toolIds: string[]): Array<{
   112→    name: string;
   113→    description: string;
   114→    input_schema: {
   115→      type: 'object';
   116→      properties: Record<string, any>;
   117→      required: string[];
   118→    };
   119→  }> {
   120→    return toolIds
   121→      .map(id => this.getTool(id))
   122→      .filter((tool): tool is AgentTool => tool !== undefined && tool.enabled)
   123→      .map(tool => tool.toAnthropicTool());
   124→  }
   125→
   126→  /**
   127→   * Execute a tool with given parameters and context
   128→   */
   129→  async executeTool(
   130→    toolId: string,
   131→    params: any,
   132→    context: ToolExecutionContext
   133→  ): Promise<ToolExecutionResult> {
   134→    const tool = this.getTool(toolId);
   135→
   136→    if (!tool) {
   137→      return {
   138→        success: false,
   139→        error: `Tool '${toolId}' not found in registry`
   140→      };
   141→    }
   142→
   143→    if (!tool.enabled) {
   144→      return {
   145→        success: false,
   146→        error: `Tool '${toolId}' is disabled`
   147→      };
   148→    }
   149→
   150→    // Check permissions if required
   151→    if (tool.requiresPermission) {
   152→      // TODO: Implement permission checking logic
   153→      // For now, we'll skip permission checks
   154→    }
   155→
   156→    try {
   157→      const result = await tool.execute(params, context);
   158→      return result;
   159→    } catch (error: any) {
   160→      return {
   161→        success: false,
   162→        error: `Tool execution failed: ${error.message}`,
   163→        metadata: {
   164→          executionTimeMs: 0
   165→        }
   166→      };
   167→    }
   168→  }
   169→
   170→  /**
   171→   * Get tool count by category
   172→   */
   173→  getToolStats(): Record<string, number> {
   174→    const stats: Record<string, number> = {
   175→      total: this.tools.size,
   176→      enabled: 0,
   177→      disabled: 0
   178→    };
   179→
   180→    const categories = new Set<string>();
   181→
   182→    this.getAllTools().forEach(tool => {
   183→      if (tool.enabled) {
   184→        stats.enabled++;
   185→      } else {
   186→        stats.disabled++;
   187→      }
   188→
   189→      categories.add(tool.category);
   190→      stats[tool.category] = (stats[tool.category] || 0) + 1;
   191→    });
   192→
   193→    return stats;
   194→  }
   195→
   196→  /**
   197→   * Search tools by keyword in name or description
   198→   */
   199→  searchTools(keyword: string): AgentTool[] {
   200→    const lowerKeyword = keyword.toLowerCase();
   201→    return this.getAllTools().filter(tool =>
   202→      tool.name.toLowerCase().includes(lowerKeyword) ||
   203→      tool.description.toLowerCase().includes(lowerKeyword) ||
   204→      tool.id.toLowerCase().includes(lowerKeyword)
   205→    );
   206→  }
   207→}
   208→
   209→/**
   210→ * Singleton instance of the tool registry
   211→ */
   212→export const toolRegistry = new ToolRegistry();
   213→
   214→/**
   215→ * Helper function to seed the ai_agent_tools table with all registered tools
   216→ */
   217→export async function loadToolsToDatabase(): Promise<{
   218→  success: boolean;
   219→  toolsLoaded: number;
   220→  errors: string[];
   221→}> {
   222→  const supabase = createAdminClient();
   223→  const allTools = toolRegistry.getAllTools();
   224→  const errors: string[] = [];
   225→  let toolsLoaded = 0;
   226→
   227→  for (const tool of allTools) {
   228→    try {
   229→      const { error } = await supabase
   230→        .from('ai_agent_tools')
   231→        .insert({
   232→          id: tool.id,
   233→          name: tool.name,
   234→          description: tool.description,
   235→          category: tool.category,
   236→          parameters_schema: tool.toOpenAIFunction().parameters,
   237→          requires_permission: tool.requiresPermission || null,
   238→          is_system: tool.isSystem,
   239→          enabled: tool.enabled,
   240→          metadata: {
   241→            last_synced: new Date().toISOString()
   242→          }
   243→        });
   244→
   245→      if (error) {
   246→        // If tool already exists, that's okay
   247→        if (!error.message.includes('duplicate key')) {
   248→          errors.push(`${tool.id}: ${error.message}`);
   249→        }
   250→      } else {
   251→        toolsLoaded++;
   252→      }
   253→    } catch (err: any) {
   254→      errors.push(`${tool.id}: ${err.message}`);
   255→    }
   256→  }
   257→
   258→  return {
   259→    success: errors.length === 0,
   260→    toolsLoaded,
   261→    errors
   262→  };
   263→}
   264→
   265→/**
   266→ * Helper function to sync existing tools in database with current registry
   267→ * Updates metadata, descriptions, and parameters for existing tools
   268→ */
   269→export async function syncToolsToDatabase(): Promise<{
   270→  success: boolean;
   271→  toolsUpdated: number;
   272→  toolsCreated: number;
   273→  errors: string[];
   274→}> {
   275→  const supabase = createAdminClient();
   276→  const allTools = toolRegistry.getAllTools();
   277→  const errors: string[] = [];
   278→  let toolsUpdated = 0;
   279→  let toolsCreated = 0;
   280→
   281→  for (const tool of allTools) {
   282→    try {
   283→      // Try to update existing tool
   284→      const { error: updateError, count } = await supabase
   285→        .from('ai_agent_tools')
   286→        .update({
   287→          name: tool.name,
   288→          description: tool.description,
   289→          category: tool.category,
   290→          parameters_schema: tool.toOpenAIFunction().parameters,
   291→          requires_permission: tool.requiresPermission || null,
   292→          is_system: tool.isSystem,
   293→          enabled: tool.enabled,
   294→          metadata: {
   295→            last_synced: new Date().toISOString()
   296→          },
   297→          updated_at: new Date().toISOString()
   298→        })
   299→        .eq('id', tool.id);
   300→
   301→      if (updateError) {
   302→        errors.push(`${tool.id}: ${updateError.message}`);
   303→        continue;
   304→      }
   305→
