   121→        return {
   122→          success: false,
   123→          error: "Access denied",
   124→          cost: this.emptyCost(),
   125→        };
   126→      }
   127→
   128→      const agent = conversation.agent as any;
   129→
   130→      // 2. Save user message
   131→      const { data: userMsg, error: userMsgError } = await this.supabase
   132→        .from("ai_agent_messages")
   133→        .insert({
   134→          conversation_id: conversationId,
   135→          role: "user",
   136→          content: userMessage,
   137→          tokens_used: 0,
   138→          cost_usd: 0,
   139→        })
   140→        .select()
   141→        .single();
   142→
   143→      if (userMsgError || !userMsg) {
   144→        return {
   145→          success: false,
   146→          error: "Failed to save user message",
   147→          cost: this.emptyCost(),
   148→        };
   149→      }
   150→
   151→      // 3. Get conversation history (limit to last 100 messages to prevent unbounded growth)
   152→      const { data: messageHistory, error: historyError } = await this.supabase
   153→        .from("ai_agent_messages")
   154→        .select("*")
   155→        .eq("conversation_id", conversationId)
   156→        .order("created_at", { ascending: false })
   157→        .limit(100);
   158→
   159→      if (historyError) {
   160→        return {
   161→          success: false,
   162→          error: "Failed to load conversation history",
   163→          cost: this.emptyCost(),
   164→        };
   165→      }
   166→
   167→      // Reverse to chronological order (oldest first) for provider execution
   168→      const messages = (messageHistory || []).reverse();
   169→
   170→      // 4. Get allowed tools for agent
   171→      const allowedTools = agent.allowed_tools || [];
   172→      const tools = agent.model.startsWith('gpt-')
   173→        ? this.toolRegistry.getToolsForOpenAI(allowedTools)
   174→        : this.toolRegistry.getToolsForAnthropic(allowedTools);
   175→
   176→      // 5. Execute with appropriate provider
   177→      const executionResult = await this.executeConversation(
   178→        agent,
   179→        messages,
   180→        tools
   181→      );
   182→
