   184→        return {
   185→          success: false,
   186→          error: executionResult.error,
   187→          cost: executionResult.cost,
   188→        };
   189→      }
   190→
   191→      // 6. Save assistant message
   192→      const { data: assistantMsg, error: assistantMsgError } = await this.supabase
   193→        .from("ai_agent_messages")
   194→        .insert({
   195→          conversation_id: conversationId,
   196→          role: "assistant",
   197→          content: executionResult.content || "",
   198→          tool_calls: executionResult.tool_calls || null,
   199→          tokens_used: executionResult.cost.totalTokens,
   200→          cost_usd: executionResult.cost.costBilledCents / 100,
   201→          model: agent.model,
   202→        })
   203→        .select()
   204→        .single();
   205→
   206→      if (assistantMsgError || !assistantMsg) {
   207→        return {
   208→          success: false,
   209→          error: "Failed to save assistant message",
   210→          cost: executionResult.cost,
   211→        };
   212→      }
   213→
   214→      // 7. Update conversation stats
   215→      const newTotalTokens = (conversation.total_tokens_used || 0) + executionResult.cost.totalTokens;
   216→      const newTotalCost = (conversation.total_cost_usd || 0) + executionResult.cost.costBilledCents / 100;
   217→
   218→      await this.supabase
   219→        .from("ai_agent_conversations")
