   222→          message_count: (conversation.message_count || 0) + 2,
   223→          total_tokens_used: newTotalTokens,
   224→          total_cost_usd: newTotalCost,
   225→          updated_at: new Date().toISOString(),
   226→        })
   227→        .eq("id", conversationId);
   228→
   229→      // 8. Log AI usage for billing
   230→      await logAIUsage({
   231→        organizationId,
   232→        agentId: agent.id,
   233→        conversationId,
   234→        actionType: "conversation_message",
   235→        model: agent.model,
   236→        inputTokens: executionResult.cost.inputTokens,
   237→        outputTokens: executionResult.cost.outputTokens,
   238→        totalTokens: executionResult.cost.totalTokens,
   239→        costBaseUsd: executionResult.cost.costBaseCents / 100,
   240→        costBilledUsd: executionResult.cost.costBilledCents / 100,
   241→        executionTimeMs: Date.now() - startTime,
   242→        metadata: {
   243→          userId,
   244→          messageCount: conversation.message_count + 2,
   245→        },
   246→      });
   247→
   248→      return {
   249→        success: true,
   250→        userMessage: {
   251→          id: userMsg.id,
   252→          role: "user",
   253→          content: userMsg.content || "",
   254→          created_at: userMsg.created_at,
   255→        },
   256→        assistantMessage: {
   257→          id: assistantMsg.id,
   258→          role: "assistant",
   259→          content: assistantMsg.content || "",
   260→          tool_calls: assistantMsg.tool_calls,
   261→          tokens_used: assistantMsg.tokens_used || 0,
   262→          cost_usd: assistantMsg.cost_usd || 0,
   263→          created_at: assistantMsg.created_at,
   264→        },
   265→        cost: executionResult.cost,
   266→      };
   267→    } catch (error: any) {
   268→      console.error("Error executing conversation message:", error);
   269→      return {
   270→        success: false,
   271→        error: error.message || "Failed to execute message",
   272→        cost: this.emptyCost(),
   273→      };
   274→    }
   275→  }
   276→
   277→  /**
   278→   * Execute conversation with appropriate AI provider
   279→   */
   280→  private async executeConversation(
   281→    agent: any,
   282→    messageHistory: any[],
   283→    tools: any[]
   284→  ): Promise<{
   285→    success: boolean;
   286→    content?: string;
   287→    tool_calls?: any;
   288→    cost: CostCalculation;
   289→    error?: string;
   290→  }> {
   291→    const model = agent.model;
   292→
   293→    // Determine provider from model
   294→    if (model.startsWith("gpt-")) {
   295→      return this.executeConversationOpenAI(agent, messageHistory, tools);
   296→    } else if (model.startsWith("claude-")) {
   297→      return this.executeConversationAnthropic(agent, messageHistory, tools);
   298→    } else {
   299→      return {
   300→        success: false,
   301→        error: `Unsupported model: ${model}`,
   302→        cost: this.emptyCost(),
   303→      };
   304→    }
   305→  }
   306→
   307→  /**
   308→   * Execute conversation with OpenAI
   309→   */
   310→  private async executeConversationOpenAI(
   311→    agent: any,
   312→    messageHistory: any[],
   313→    tools: any[]
   314→  ): Promise<{
   315→    success: boolean;
   316→    content?: string;
   317→    tool_calls?: any;
   318→    cost: CostCalculation;
   319→    error?: string;
   320→  }> {
   321→    const provider = new OpenAIProvider();
   322→
   323→    const messages = [
   324→      { role: "system" as const, content: agent.system_prompt },
   325→      ...messageHistory.map((msg) => ({
   326→        role: msg.role as "user" | "assistant" | "system" | "tool",
   327→        content: msg.content || null,
   328→        tool_calls: msg.tool_calls,
   329→        tool_call_id: msg.tool_call_id,
   330→      })),
   331→    ];
   332→
   333→    const result = await provider.execute(messages, {
   334→      model: agent.model,
   335→      temperature: agent.temperature ?? 0.7,
   336→      max_tokens: agent.max_tokens ?? 4096,
   337→      tools: tools.length > 0 ? tools : undefined,
   338→      tool_choice: tools.length > 0 ? "auto" : undefined,
   339→    });
   340→
   341→    if (!result.success) {
   342→      return {
   343→        success: false,
   344→        error: result.error,
   345→        cost: result.cost,
   346→      };
   347→    }
   348→
   349→    return {
   350→      success: true,
   351→      content: result.message?.content || "",
   352→      tool_calls: result.toolCalls,
   353→      cost: result.cost,
   354→    };
   355→  }
   356→
   357→  /**
   358→   * Execute conversation with Anthropic
   359→   */
   360→  private async executeConversationAnthropic(
   361→    agent: any,
   362→    messageHistory: any[],
   363→    tools: any[]
   364→  ): Promise<{
   365→    success: boolean;
   366→    content?: string;
   367→    tool_calls?: any;
   368→    cost: CostCalculation;
   369→    error?: string;
   370→  }> {
   371→    const provider = new AnthropicProvider();
   372→
   373→    const messages = messageHistory
   374→      .filter((msg) => msg.role !== "system")
   375→      .map((msg) => {
   376→        if (msg.role === "tool") {
   377→          return {
   378→            role: "user" as const,
   379→            content: [
   380→              {
   381→                type: "tool_result" as const,
   382→                tool_use_id: msg.tool_call_id,
   383→                content: msg.content,
   384→              },
   385→            ],
   386→          };
   387→        }
   388→
   389→        return {
   390→          role: msg.role as "user" | "assistant",
   391→          content: msg.content || "",
   392→        };
   393→      });
   394→
   395→    const result = await provider.execute(messages, {
   396→      model: agent.model,
   397→      temperature: agent.temperature ?? 0.7,
   398→      max_tokens: agent.max_tokens ?? 4096,
   399→      system: agent.system_prompt,
   400→      tools: tools.length > 0 ? tools : undefined,
   401→    });
   402→
   403→    if (!result.success) {
   404→      return {
   405→        success: false,
   406→        error: result.error,
   407→        cost: result.cost,
   408→      };
   409→    }
   410→
   411→    const textContent = result.content
   412→      ?.filter((block: any) => block.type === "text")
   413→      .map((block: any) => block.text)
   414→      .join("") || "";
   415→
   416→    const toolUses = result.content
   417→      ?.filter((block: any) => block.type === "tool_use")
   418→      .map((block: any) => ({
   419→        id: block.id,
   420→        name: block.name,
   421→        input: block.input,
   422→      })) || [];
   423→
   424→    return {
   425→      success: true,
   426→      content: textContent,
   427→      tool_calls: toolUses.length > 0 ? toolUses : undefined,
   428→      cost: result.cost,
   429→    };
   430→  }
   431→
   432→  /**
   433→   * Empty cost object for errors
   434→   */
   435→  private emptyCost(): CostCalculation {
   436→    return {
   437→      model: "unknown",
   438→      inputTokens: 0,
   439→      outputTokens: 0,
   440→      totalTokens: 0,
   441→      costBaseCents: 0,
   442→      costBilledCents: 0,
   443→      markupPercentage: 20,
   444→    };
   445→  }
   446→
   447→  /**
   448→   * Execute a task by ID
   449→   */
   450→  async executeTask(taskId: string): Promise<TaskExecutionResult> {
   451→    const startTime = Date.now();
   452→    let tokensUsed = 0;
   453→    let costUsd = 0;
   454→
   455→    try {
   456→      // Check global rate limit first
   457→      const globalOk = await checkGlobalRateLimit();
   458→      if (!globalOk) {
   459→        throw new Error('Global rate limit exceeded. System is at capacity.');
   460→      }
   461→
   462→      // Fetch task details
   463→      const { data: task, error: taskError } = await this.supabase
   464→        .from('ai_agent_tasks')
   465→        .select('*')
   466→        .eq('id', taskId)
   467→        .single();
   468→
   469→      if (taskError || !task) {
   470→        throw new Error(`Task not found: ${taskId}`);
   471→      }
   472→
   473→      // Check organization-specific rate limit
   474→      const orgOk = await checkOrgRateLimit(task.organization_id);
   475→      if (!orgOk) {
   476→        throw new Error('Organization rate limit exceeded. Please try again in 1 minute.');
   477→      }
   478→
   479→      // Fetch agent details
   480→      const { data: agent, error: agentError } = await this.supabase
   481→        .from('ai_agents')
   482→        .select('*')
   483→        .eq('id', task.agent_id)
   484→        .single();
   485→
   486→      if (agentError || !agent) {
   487→        throw new Error(`Agent not found: ${task.agent_id}`);
   488→      }
   489→
   490→      // Check agent-specific rate limit
   491→      const agentOk = await checkAgentRateLimit(agent.id);
   492→      if (!agentOk) {
   493→        throw new Error(`Agent rate limit exceeded for ${agent.name}. Please try again in 1 minute.`);
   494→      }
   495→
   496→      // Update task status to running
   497→      await this.updateTaskStatus(taskId, 'running');
   498→
   499→      // Execute the task
   500→      const result = await this.executeAgentTask(agent, task);
   501→
   502→      tokensUsed = result.tokensUsed;
   503→      costUsd = result.costUsd;
   504→
   505→      // Update task with result
   506→      await this.supabase
   507→        .from('ai_agent_tasks')
   508→        .update({
   509→          status: 'completed',
   510→          result: result.result,
   511→          tokens_used: tokensUsed,
   512→          cost_usd: costUsd,
   513→          execution_time_ms: result.executionTimeMs,
   514→          completed_at: new Date().toISOString(),
   515→          updated_at: new Date().toISOString()
   516→        })
   517→        .eq('id', taskId);
   518→
   519→      // Log activity
   520→      await this.logActivity(agent.id, agent.organization_id, taskId, {
   521→        action_type: 'task_completed',
   522→        action_data: { task_title: task.title },
   523→        tokens_used: tokensUsed,
   524→        cost_usd: costUsd,
   525→        execution_time_ms: result.executionTimeMs,
   526→        success: true
   527→      });
   528→
   529→      return {
   530→        success: true,
   531→        result: result.result,
   532→        tokensUsed,
   533→        costUsd,
   534→        executionTimeMs: Date.now() - startTime
   535→      };
   536→
   537→    } catch (error) {
   538→      const executionTimeMs = Date.now() - startTime;
   539→      const errorMessage = error instanceof Error ? error.message : 'Unknown error';
   540→
   541→      // Update task as failed
   542→      await this.supabase
   543→        .from('ai_agent_tasks')
   544→        .update({
   545→          status: 'failed',
   546→          error_message: errorMessage,
   547→          execution_time_ms: executionTimeMs,
   548→          updated_at: new Date().toISOString()
   549→        })
   550→        .eq('id', taskId);
   551→
   552→      return {
   553→        success: false,
   554→        error: errorMessage,
   555→        tokensUsed,
   556→        costUsd,
   557→        executionTimeMs
   558→      };
   559→    }
   560→  }
   561→
   562→  /**
   563→   * Execute agent task with provider
   564→   */
   565→  private async executeAgentTask(
   566→    agent: Agent,
   567→    task: AgentTask
   568→  ): Promise<TaskExecutionResult> {
   569→    const startTime = Date.now();
   570→
   571→    // Execute based on model provider
   572→    let result: any;
   573→    let tokensUsed = 0;
   574→    let costUsd = 0;
   575→
   576→    if (agent.model.startsWith('gpt-')) {
   577→      // OpenAI
   578→      const provider = new OpenAIProvider();
   579→      const tools = this.toolRegistry.getToolsForOpenAI(agent.allowed_tools);
   580→
   581→      const response = await provider.execute(
   582→        [
   583→          {
   584→            role: 'system',
   585→            content: agent.system_prompt
   586→          },
   587→          {
   588→            role: 'user',
   589→            content: `${task.title}\n\n${task.description || ''}\n\nContext: ${JSON.stringify(task.context, null, 2)}`
   590→          }
   591→        ],
   592→        {
   593→          model: agent.model,
   594→          temperature: agent.temperature,
   595→          max_tokens: agent.max_tokens,
   596→          tools: tools.length > 0 ? tools : undefined
   597→        }
   598→      );
   599→
   600→      if (!response.success) {
   601→        throw new Error(response.error || 'OpenAI execution failed');
   602→      }
   603→
   604→      tokensUsed = response.cost.totalTokens;
   605→      costUsd = response.cost.costBaseCents / 100;
   606→
   607→      result = {
   608→        content: response.message?.content || '',
   609→        tool_calls: response.toolCalls || []
   610→      };
   611→
   612→    } else if (agent.model.startsWith('claude-')) {
   613→      // Anthropic
   614→      const provider = new AnthropicProvider();
   615→      const tools = this.toolRegistry.getToolsForAnthropic(agent.allowed_tools);
   616→
   617→      const response = await provider.execute(
   618→        [
   619→          {
   620→            role: 'user',
   621→            content: `${task.title}\n\n${task.description || ''}\n\nContext: ${JSON.stringify(task.context, null, 2)}`
   622→          }
   623→        ],
   624→        {
   625→          model: agent.model,
   626→          max_tokens: agent.max_tokens,
   627→          temperature: agent.temperature,
   628→          system: agent.system_prompt,
   629→          tools: tools.length > 0 ? tools : undefined
   630→        }
   631→      );
   632→
   633→      if (!response.success) {
   634→        throw new Error(response.error || 'Anthropic execution failed');
   635→      }
   636→
   637→      tokensUsed = response.cost.totalTokens;
   638→      costUsd = response.cost.costBaseCents / 100;
   639→
   640→      // Extract text and tool uses from Anthropic response
   641→      const textContent = response.content
   642→        ? response.content.filter((c: any) => c.type === 'text').map((c: any) => c.text).join('')
   643→        : '';
   644→
   645→      const toolUses = response.content
   646→        ? response.content.filter((c: any) => c.type === 'tool_use')
   647→        : [];
   648→
   649→      result = {
   650→        content: textContent,
   651→        tool_calls: toolUses,
   652→        stop_reason: response.stopReason
   653→      };
   654→    } else {
   655→      throw new Error(`Unsupported model: ${agent.model}`);
   656→    }
   657→
   658→    return {
   659→      success: true,
   660→      result,
   661→      tokensUsed,
   662→      costUsd,
   663→      executionTimeMs: Date.now() - startTime
   664→    };
   665→  }
   666→
   667→  /**
   668→   * Update task status
   669→   */
   670→  private async updateTaskStatus(
   671→    taskId: string,
   672→    status: AgentTask['status']
   673→  ): Promise<void> {
   674→    await this.supabase
   675→      .from('ai_agent_tasks')
   676→      .update({
   677→        status,
   678→        updated_at: new Date().toISOString()
   679→      })
   680→      .eq('id', taskId);
   681→  }
   682→
   683→  /**
   684→   * Log agent activity
   685→   */
   686→  private async logActivity(
   687→    agentId: string,
   688→    organizationId: string,
   689→    taskId: string,
   690→    data: {
   691→      action_type: string;
   692→      action_data: Record<string, any>;
   693→      tokens_used: number;
   694→      cost_usd: number;
   695→      execution_time_ms: number;
   696→      success: boolean;
   697→      error_message?: string;
   698→    }
   699→  ): Promise<void> {
   700→    const costBilledUsd = data.cost_usd * 1.2; // 20% markup
   701→
   702→    await this.supabase
   703→      .from('ai_agent_activity_log')
   704→      .insert({
   705→        agent_id: agentId,
   706→        organization_id: organizationId,
   707→        task_id: taskId,
   708→        action_type: data.action_type,
   709→        action_data: data.action_data,
   710→        tokens_used: data.tokens_used,
   711→        cost_usd: data.cost_usd,
   712→        cost_billed_usd: costBilledUsd,
   713→        execution_time_ms: data.execution_time_ms,
   714→        success: data.success,
   715→        error_message: data.error_message
   716→      });
   717→  }
   718→}
   719→
   720→/**
   721→ * Singleton instance
   722→ */
   723→let orchestratorInstance: AgentOrchestrator | null = null;
   724→
   725→/**
   726→ * Get the orchestrator singleton instance
   727→ */
   728→export function getOrchestrator(): AgentOrchestrator {
   729→  if (!orchestratorInstance) {
   730→    orchestratorInstance = new AgentOrchestrator();
   731→  }
   732→  return orchestratorInstance;
   733→}
   734→
   735→// Export singleton instance for backwards compatibility
   736→export const agentOrchestrator = getOrchestrator();
   737→

