   330→
   331→  requiresPermission = 'messages:send';
   332→
   333→  async execute(params: any, context: ToolExecutionContext): Promise<ToolExecutionResult> {
   334→    const startTime = Date.now();
   335→    const validated = this.parametersSchema.parse(params);
   336→
   337→    try {
   338→      const supabase = createAdminClient();
   339→
   340→      // Fetch client details
   341→      const { data: client, error: clientError } = await supabase
   342→        .from('clients')
   343→        .select('id, name, email, phone')
   344→        .eq('id', validated.clientId)
   345→        .eq('organization_id', context.organizationId)
   346→        .single();
   347→
   348→      if (clientError || !client) {
   349→        return { success: false, error: 'Client not found' };
   350→      }
   351→
   352→      // Validate contact info
   353→      if (validated.channel === 'email' && !client.email) {
   354→        return { success: false, error: 'Client has no email address' };
   355→      }
   356→      if ((validated.channel === 'sms' || validated.channel === 'whatsapp') && !client.phone) {
   357→        return { success: false, error: 'Client has no phone number' };
   358→      }
   359→      if (validated.channel === 'email' && !validated.subject) {
   360→        return { success: false, error: 'Email subject is required' };
   361→      }
   362→
   363→      // Replace {clientName} with actual name
   364→      const personalizedMessage = validated.message.replace(/{clientName}/g, client.name);
   365→
   366→      // Log message to database
   367→      const { data: loggedMessage, error: logError } = await supabase
   368→        .from('messages')
   369→        .insert({
   370→          organization_id: context.organizationId,
   371→          client_id: client.id,
   372→          type: validated.channel,
   373→          channel: validated.channel,
   374→          direction: 'outbound',
   375→          status: 'pending',
   376→          subject: validated.subject,
   377→          body: personalizedMessage,
   378→          content: personalizedMessage,
   379→          sender_type: 'ai',
   380→          sender_name: `AI Agent (${context.agentId})`,
   381→          metadata: {
   382→            conversationId: context.conversationId,
   383→            taskId: context.taskId,
