   385→        })
   386→        .select()
   387→        .single();
   388→
   389→      if (logError) throw logError;
   390→
   391→      // Send via appropriate channel
   392→      let sendResult: any = null;
   393→
   394→      if (validated.channel === 'email' && process.env.RESEND_API_KEY) {
   395→        const resend = new Resend(process.env.RESEND_API_KEY);
   396→        sendResult = await resend.emails.send({
   397→          from: process.env.RESEND_FROM_EMAIL || 'Atlas Fitness <noreply@atlas-fitness.com>',
   398→          to: client.email!,
   399→          subject: validated.subject!,
   400→          html: `<div style="font-family: Arial, sans-serif;">${personalizedMessage.replace(/\n/g, '<br>')}</div>`,
   401→        });
   402→
   403→        if (sendResult.data?.id) {
   404→          await supabase
   405→            .from('messages')
   406→            .update({ status: 'sent', sent_at: new Date().toISOString() })
   407→            .eq('id', loggedMessage.id);
   408→        }
   409→      } else if ((validated.channel === 'sms' || validated.channel === 'whatsapp') &&
   410→                 process.env.TWILIO_ACCOUNT_SID && process.env.TWILIO_AUTH_TOKEN) {
   411→        const twilio = (await import('twilio')).default;
   412→        const twilioClient = twilio(process.env.TWILIO_ACCOUNT_SID, process.env.TWILIO_AUTH_TOKEN);
   413→
   414→        const fromNumber = validated.channel === 'whatsapp'
   415→          ? `whatsapp:${process.env.TWILIO_WHATSAPP_NUMBER}`
   416→          : process.env.TWILIO_PHONE_NUMBER;
   417→
   418→        const toNumber = validated.channel === 'whatsapp'
   419→          ? `whatsapp:${client.phone}`
   420→          : client.phone;
   421→
   422→        sendResult = await twilioClient.messages.create({
   423→          body: personalizedMessage,
   424→          from: fromNumber,
   425→          to: toNumber!,
   426→        });
   427→
   428→        if (sendResult.sid) {
   429→          await supabase
   430→            .from('messages')
   431→            .update({ status: 'sent', sent_at: new Date().toISOString() })
   432→            .eq('id', loggedMessage.id);
   433→        }
   434→      }
   435→
   436→      return {
   437→        success: true,
   438→        data: {
   439→          messageId: loggedMessage.id,
   440→          clientId: client.id,
   441→          clientName: client.name,
   442→          channel: validated.channel,
   443→          status: 'sent',
   444→        },
   445→        metadata: {
   446→          executionTimeMs: Date.now() - startTime,
   447→        },
   448→      };
   449→
   450→    } catch (error: any) {
   451→      return {
   452→        success: false,
   453→        error: error.message,
   454→        metadata: {
   455→          executionTimeMs: Date.now() - startTime,
   456→        },
   457→      };
   458→    }
   459→  }
