// Process account list for parallel monitoring
const allAccountsData = [];

// Process each ad account
for (const account of $input.all()) {
  const accountId = account.json.id;
  const accountName = account.json.name;

  // Store account info for parallel processing
  allAccountsData.push({
    id: accountId,
    name: accountName,
    originalData: account.json
  });
}

// Return account data for parallel processing
return allAccountsData.map(account => ({ json: account }));

// Import required modules
const MetaDataProcessor = require('./lib/meta-data-processor');
const config = require('./config/thresholds');
const processor = new MetaDataProcessor();

// Get all input data
const allData = $input.all();

// Group data by account
const groupedData = {};
const criticalIssues = [];

for (const item of allData) {
  const accountId = item.json.account_id || item.json.id;
  const accountName = item.json.account_name || item.json.name;

  if (!groupedData[accountId]) {
    groupedData[accountId] = {
      id: accountId,
      name: accountName,
      campaigns: []
    };
  }

  // Add campaign data
  if (item.json.data) {
    groupedData[accountId].campaigns = item.json.data;
  }
}

// Process each account for critical issues
for (const [accountId, accountData] of Object.entries(groupedData)) {
  try {
    // Process campaigns
    const processedCampaigns = processor.processCampaigns(accountData.campaigns);

    // Find critical campaigns
    const criticalCampaigns = processedCampaigns.filter(campaign => 
      campaign.severity === 'critical' && campaign.spend >= config.thresholds.minimumSpend
    );

    // Add to critical issues if found
    if (criticalCampaigns.length > 0) {
      criticalIssues.push({
        accountId: accountId,
        accountName: accountData.name,
        criticalCampaigns: criticalCampaigns,
        timestamp: new Date().toISOString()
      });
    }
  } catch (error) {
    console.error(`Error processing account ${accountId}:`, error);
  }
}

// Return critical issues for alert processing
if (criticalIssues.length > 0) {
  return criticalIssues.map(issue => ({ json: issue }));
} else {
  // No critical issues found
  return [{
    json: {
      noCriticalIssues: true,
      timestamp: new Date().toISOString(),
      accountsChecked: Object.keys(groupedData).length,
      message: 'No critical issues detected'
    }
  }];
}

// Generate AI crisis analysis for critical issues
const AIAnalyzer = require('./lib/ai-analyzer');
const config = require('./config/thresholds');

// Initialize AI analyzer
const aiAnalyzer = new AIAnalyzer(config.openai.apiKey);

// Get critical issue data
const criticalData = $input.first().json;
const { accountName, criticalCampaigns } = criticalData;

// Generate crisis analysis
let crisisAnalysis;
try {
  crisisAnalysis = await aiAnalyzer.generateCrisisAnalysis(criticalCampaigns, {
    accountName: accountName,
    timestamp: new Date().toISOString()
  });
} catch (error) {
  console.error('Crisis analysis failed:', error);
  // Use fallback analysis
  crisisAnalysis = {
    emergencyActions: [
      'Pause campaigns with CPL > ¬£40',
      'Review targeting immediately',
      'Check budget allocation'
    ],
    quickWins: [
      'Update ad creative',
      'Adjust audience targeting',
      'Review landing page'
    ],
    rootCause: 'High competition or poor targeting likely causing issues',
    nextSteps: [
      'Analyze competitor activity',
      'Review audience insights',
      'Test new creative approaches'
    ],
    timestamp: new Date().toISOString()
  };
}

// Return data with crisis analysis
return [{
  json: {
    ...criticalData,
    crisisAnalysis: crisisAnalysis
  }
}];

// Import report formatter
const ReportFormatter = require('./lib/report-formatter');
const formatter = new ReportFormatter();

// Get crisis data
const crisisData = $input.first().json;

// Format alert notification
const alertNotification = formatter.formatAlert(crisisData);

// Return formatted alert
return [{
  json: {
    ...crisisData,
    alertNotification: alertNotification
  }
}];

// Check alert cooldown to prevent spam
const fs = require('fs');
const path = require('path');

// Get alert data
const alertData = $input.first().json;
const { accountId, criticalCampaigns } = alertData;

// Create cooldown tracking directory
const cooldownDir = './storage/alert-cooldowns';
if (!fs.existsSync(cooldownDir)) {
  fs.mkdirSync(cooldownDir, { recursive: true });
}

// Check cooldown for each campaign
const config = require('./config/thresholds');
const cooldownMinutes = config.alerts.alertCooldown || 30;
const now = new Date();

const allowedCampaigns = [];

for (const campaign of criticalCampaigns) {
  const cooldownFile = path.join(cooldownDir, `${campaign.campaignId}.json`);

  try {
    if (fs.existsSync(cooldownFile)) {
      const cooldownData = JSON.parse(fs.readFileSync(cooldownFile, 'utf8'));
      const lastAlert = new Date(cooldownData.lastAlert);
      const timeSinceLastAlert = (now - lastAlert) / (1000 * 60); // minutes

      if (timeSinceLastAlert >= cooldownMinutes) {
        allowedCampaigns.push(campaign);
        // Update cooldown file
        fs.writeFileSync(cooldownFile, JSON.stringify({
          campaignId: campaign.campaignId,
          lastAlert: now.toISOString(),
          alertCount: (cooldownData.alertCount || 0) + 1
        }));
      }
    } else {
      // First alert for this campaign
      allowedCampaigns.push(campaign);
      fs.writeFileSync(cooldownFile, JSON.stringify({
        campaignId: campaign.campaignId,
        lastAlert: now.toISOString(),
        alertCount: 1
      }));
    }
  } catch (error) {
    console.error(`Error checking cooldown for campaign ${campaign.campaignId}:`, error);
    // Allow alert on error
    allowedCampaigns.push(campaign);
  }
}

// Return data with filtered campaigns
if (allowedCampaigns.length > 0) {
  return [{
    json: {
      ...alertData,
      criticalCampaigns: allowedCampaigns,
      originalCount: criticalCampaigns.length,
      allowedCount: allowedCampaigns.length
    }
  }];
} else {
  // All campaigns are in cooldown
  return [{
    json: {
      ...alertData,
      allInCooldown: true,
      message: 'All campaigns are in alert cooldown period'
    }
  }];
}

// Log alert activity and store for historical tracking
const fs = require('fs');
const path = require('path');

// Get alert data
const alertData = $input.first().json;

// Create alerts log directory
const alertsDir = './storage/alerts';
if (!fs.existsSync(alertsDir)) {
  fs.mkdirSync(alertsDir, { recursive: true });
}

// Generate alert log entry
const alertLogEntry = {
  timestamp: new Date().toISOString(),
  accountId: alertData.accountId,
  accountName: alertData.accountName,
  criticalCampaigns: alertData.criticalCampaigns.map(c => ({
    campaignId: c.campaignId,
    campaignName: c.campaignName,
    costPerLead: c.costPerLead,
    spend: c.spend,
    leads: c.leads,
    issues: c.issues
  })),
  crisisAnalysis: alertData.crisisAnalysis,
  alertType: 'critical_performance'
};

// Save alert log
const today = new Date().toISOString().split('T')[0];
const alertLogFile = path.join(alertsDir, `alerts-${today}.json`);

try {
  let alertLog = [];
  if (fs.existsSync(alertLogFile)) {
    alertLog = JSON.parse(fs.readFileSync(alertLogFile, 'utf8'));
  }

  alertLog.push(alertLogEntry);
  fs.writeFileSync(alertLogFile, JSON.stringify(alertLog, null, 2));

  console.log(`Alert logged: ${alertData.accountName} - ${alertData.criticalCampaigns.length} campaigns`);
} catch (error) {
  console.error('Error logging alert:', error);
}

// Return success status
return [{
  json: {
    success: true,
    message: 'Alert sent successfully',
    timestamp: new Date().toISOString(),
    accountName: alertData.accountName,
    criticalCampaigns: alertData.criticalCampaigns.length,
    alertsSent: {
      email: true,
      telegram: true
    }
  }
}];

// Log successful monitoring run without alerts
const monitoringData = $input.first().json;

console.log(`Real-time monitoring completed: ${monitoringData.accountsChecked} accounts checked, no critical issues found`);

// Return status
return [{
  json: {
    success: true,
    message: 'Monitoring completed - no critical issues',
    timestamp: new Date().toISOString(),
    accountsChecked: monitoringData.accountsChecked,
    criticalIssues: 0
  }
}];

// Log campaigns in cooldown
const cooldownData = $input.first().json;

console.log(`Campaigns in cooldown: ${cooldownData.accountName} - ${cooldownData.originalCount} issues found, ${cooldownData.allowedCount} alerts allowed`);

// Return status
return [{
  json: {
    success: true,
    message: 'Campaigns in alert cooldown',
    timestamp: new Date().toISOString(),
    accountName: cooldownData.accountName,
    campaignsInCooldown: cooldownData.originalCount - cooldownData.allowedCount
  }
}];

// Error handling for real-time monitoring
const error = $input.first().json.error;
const context = $input.first().json.context || 'Real-time monitoring';

console.error(`Real-time monitoring error in ${context}:`, error);

// Send error notification
const errorMessage = `üö® *Real-Time Monitoring Error*\n\nContext: ${context}\nError: ${error.message || error}\n\nTime: ${new Date().toLocaleString('en-GB')}\n\n_Next check in 2 hours_`;

return [{
  json: {
    chatId: process.env.TELEGRAM_CHAT_ID,
    text: errorMessage,
    parseMode: 'Markdown'
  }
}];

// Rate limiting check to prevent too many alerts
const fs = require('fs');
const path = require('path');

// Get alert data
const alertData = $input.first().json;

// Create rate limiting directory
const rateLimitDir = './storage/rate-limits';
if (!fs.existsSync(rateLimitDir)) {
  fs.mkdirSync(rateLimitDir, { recursive: true });
}

// Check hourly rate limit
const config = require('./config/thresholds');
const maxAlertsPerHour = config.alerts.maxAlertsPerHour || 10;
const now = new Date();
const currentHour = now.getHours();
const today = now.toISOString().split('T')[0];

const rateLimitFile = path.join(rateLimitDir, `rate-limit-${today}.json`);

try {
  let rateLimitData = {};
  if (fs.existsSync(rateLimitFile)) {
    rateLimitData = JSON.parse(fs.readFileSync(rateLimitFile, 'utf8'));
  }

  const hourKey = `hour_${currentHour}`;
  const alertsThisHour = rateLimitData[hourKey] || 0;

  if (alertsThisHour >= maxAlertsPerHour) {
    // Rate limit exceeded
    return [{
      json: {
        ...alertData,
        rateLimitExceeded: true,
        message: `Rate limit exceeded: ${alertsThisHour}/${maxAlertsPerHour} alerts sent this hour`
      }
    }];
  } else {
    // Update rate limit counter
    rateLimitData[hourKey] = alertsThisHour + 1;
    fs.writeFileSync(rateLimitFile, JSON.stringify(rateLimitData, null, 2));

    // Allow alert
    return [{
      json: {
        ...alertData,
        rateLimitOk: true,
        alertsThisHour: alertsThisHour + 1
      }
    }];
  }
} catch (error) {
  console.error('Error checking rate limit:', error);
  // Allow alert on error
  return [{
    json: {
      ...alertData,
      rateLimitOk: true,
      rateLimitError: true
    }
  }];
}

// Log rate limit exceeded
const rateLimitData = $input.first().json;

console.log(`Rate limit exceeded: ${rateLimitData.message}`);

// Send rate limit notification once per hour
const rateLimitMessage = `‚ö†Ô∏è *Alert Rate Limit Exceeded*\n\n${rateLimitData.message}\n\nCritical alerts are being throttled to prevent spam.\n\nTime: ${new Date().toLocaleString('en-GB')}`;

return [{
  json: {
    chatId: process.env.TELEGRAM_CHAT_ID,
    text: rateLimitMessage,
    parseMode: 'Markdown'
  }
}];
